sections.script:
import IconComponent from './IconComponent.vue'

export default {
  name: 'ChatScreen',
  components: {
    IconComponent
  },
  props: {
    chatUser: {
      type: Object,
      required: true
    },
    currentUser: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      newMessage: '',
      showAttachmentOptions: false,
      showUserInfo: false,
      isTyping: false,
      isMuted: false,
      typingTimeout: null,
      messages: []
    }
  },
  mounted() {
    this.loadMessages()
    this.scrollToBottom()
    // Simular status online do usu√°rio
    this.simulateUserActivity()
  },
  methods: {
    loadMessages() {
      // Carregar mensagens do localStorage ou simular algumas
      const chatId = this.getChatId()
      const savedMessages = localStorage.getItem(`ifwave_chat_${chatId}`)
      
      if (savedMessages) {
        this.messages = JSON.parse(savedMessages)
      } else {
        // Simular algumas mensagens iniciais
        this.messages = [
          {
            id: 1,
            text: 'Oi! Como est√° indo com os estudos?',
            timestamp: Date.now() - 3600000, // 1 hora atr√°s
            isOwn: false,
            status: 'read'
          },
          {
            id: 2,
            text: 'Oi! Est√° indo bem, obrigado por perguntar. E voc√™?',
            timestamp: Date.now() - 3300000, // 55 min atr√°s
            isOwn: true,
            status: 'read'
          },
          {
            id: 3,
            text: 'Tamb√©m est√° bem! Voc√™ viu o trabalho que o professor passou?',
            timestamp: Date.now() - 3000000, // 50 min atr√°s
            isOwn: false,
            status: 'read'
          }
        ]
        this.saveMessages()
      }
    },

    getChatId() {
      // Gerar ID √∫nico para a conversa baseado nos IDs dos usu√°rios
      const ids = [this.currentUser.id, this.chatUser.id].sort()
      return `${ids[0]}_${ids[1]}`
    },

    saveMessages() {
      const chatId = this.getChatId()
      localStorage.setItem(`ifwave_chat_${chatId}`, JSON.stringify(this.messages))
      
      // Atualizar √∫ltima mensagem na lista de conversas
      this.updateLastMessage()
    },

    updateLastMessage() {
      if (this.messages.length === 0) return
      
      const lastMessage = this.messages[this.messages.length - 1]
      const conversations = JSON.parse(localStorage.getItem('ifwave_conversations') || '[]')
      const chatId = this.getChatId()
      
      const existingConv = conversations.find(conv => conv.id === chatId)
      const conversationData = {
        id: chatId,
        userId: this.chatUser.id,
        name: this.chatUser.name,
        avatar: this.chatUser.avatar,
        course: this.chatUser.course,
        lastMessage: lastMessage.text,
        lastMessageTime: this.formatTime(lastMessage.timestamp),
        timestamp: lastMessage.timestamp,
        unread: !lastMessage.isOwn,
        unreadCount: !lastMessage.isOwn ? 1 : 0,
        online: this.chatUser.online
      }
      
      if (existingConv) {
        Object.assign(existingConv, conversationData)
      } else {
        conversations.push(conversationData)
      }
      
      // Ordenar por timestamp mais recente
      conversations.sort((a, b) => b.timestamp - a.timestamp)
      
      localStorage.setItem('ifwave_conversations', JSON.stringify(conversations))
    },

    sendMessage() {
      if (!this.newMessage.trim()) return

      const message = {
        id: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        text: this.newMessage.trim(),
        timestamp: Date.now(),
        isOwn: true,
        status: 'sending',
        senderId: this.currentUser.id,
        receiverId: this.chatUser.id
      }

      this.messages.push(message)
      this.newMessage = ''
      this.scrollToBottom()

      // Simular envio
      setTimeout(() => {
        message.status = 'sent'
        this.saveMessages()
        this.updateLastMessage()
        
        // Notificar sobre nova mensagem
        this.$emit('message-sent', {
          chatId: this.getChatId(),
          message: message,
          recipient: this.chatUser
        })
        
        // Simular resposta autom√°tica √†s vezes (apenas para demonstra√ß√£o)
        if (Math.random() > 0.7) {
          this.simulateReply()
        }
      }, 1000)

      // Simular entrega
      setTimeout(() => {
        if (message.status === 'sent') {
          message.status = 'delivered'
          this.saveMessages()
        }
      }, 2000)

      // Simular leitura
      setTimeout(() => {
        if (message.status === 'delivered') {
          message.status = 'read'
          this.saveMessages()
        }
      }, 5000)
    },

    simulateReply() {
      const replies = [
        'Entendi! üëç',
        'Obrigado pela informa√ß√£o!',
        'Vamos nos falar depois',
        'Perfeito! üòä',
        'Combinado',
        'Pode deixar!',
        'Beleza',
        'Ok, valeu!',
        '√ìtima ideia!',
        'Vou pensar sobre isso',
        'Me conta mais depois',
        'Legal! üôÇ'
      ]

      setTimeout(() => {
        this.isTyping = true
        
        setTimeout(() => {
          this.isTyping = false
          const reply = {
            id: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
            text: replies[Math.floor(Math.random() * replies.length)],
            timestamp: Date.now(),
            isOwn: false,
            status: 'delivered',
            senderId: this.chatUser.id,
            receiverId: this.currentUser.id
          }
          
          this.messages.push(reply)
          this.scrollToBottom()
          this.saveMessages()
          this.updateLastMessage()
          
          // Simular leitura da resposta
          setTimeout(() => {
            reply.status = 'read'
            this.saveMessages()
          }, 2000)
        }, 1500 + Math.random() * 2000) // Tempo vari√°vel de digita√ß√£o
      }, 500 + Math.random() * 1500) // Tempo antes de come√ßar a digitar
    },

    handleTyping() {
      // Simular indicador de digita√ß√£o
      clearTimeout(this.typingTimeout)
      this.typingTimeout = setTimeout(() => {
        // Parar de mostrar "digitando"
      }, 1000)
    },

    scrollToBottom() {
      this.$nextTick(() => {
        const messagesArea = this.$refs.messagesArea
        if (messagesArea) {
          messagesArea.scrollTop = messagesArea.scrollHeight
        }
      })
    },

    formatTime(timestamp) {
      const date = new Date(timestamp)
      const now = new Date()
      const diffInHours = (now - date) / (1000 * 60 * 60)
      
      if (diffInHours < 24) {
        return date.toLocaleTimeString('pt-BR', { 
          hour: '2-digit', 
          minute: '2-digit' 
        })
      } else if (diffInHours < 168) { // 7 dias
        return date.toLocaleDateString('pt-BR', { 
          weekday: 'short',
          hour: '2-digit', 
          minute: '2-digit' 
        })
      } else {
        return date.toLocaleDateString('pt-BR', {
          day: '2-digit',
          month: '2-digit',
          hour: '2-digit', 
          minute: '2-digit' 
        })
      }
    },

    getStatusIcon(status) {
      switch (status) {
        case 'sent': return 'check'
        case 'delivered': return 'check-double'
        case 'read': return 'check-double'
        default: return 'check'
      }
    },

    toggleUserInfo() {
      this.showUserInfo = !this.showUserInfo
    },

    simulateUserActivity() {
      // Simular mudan√ßas de status online/offline usando uma vari√°vel local
      setInterval(() => {
        // Emitir evento para o componente pai atualizar o status
        this.$emit('user-status-change', {
          userId: this.chatUser.id,
          online: Math.random() > 0.3
        })
      }, 30000) // A cada 30 segundos
    },

    viewProfile() {
      this.$emit('view-profile', this.chatUser)
    },

    muteConversation() {
      this.isMuted = !this.isMuted
      // Salvar prefer√™ncia no localStorage
      const chatId = this.getChatId()
      localStorage.setItem(`ifwave_muted_${chatId}`, this.isMuted.toString())
    },

    blockUser() {
      if (confirm(`Tem certeza que deseja bloquear ${this.chatUser.name}?`)) {
        // Implementar l√≥gica de bloqueio
        const blockedUsers = JSON.parse(localStorage.getItem('ifwave_blocked_users') || '[]')
        if (!blockedUsers.includes(this.chatUser.id)) {
          blockedUsers.push(this.chatUser.id)
          localStorage.setItem('ifwave_blocked_users', JSON.stringify(blockedUsers))
        }
        this.$emit('user-blocked', this.chatUser)
      }
    },

    selectImage() {
      // Implementar sele√ß√£o de imagem
      this.showAttachmentOptions = false
    },

    selectDocument() {
      // Implementar sele√ß√£o de documento
      this.showAttachmentOptions = false
    }
  }
}

sections.style:
.chat-container {
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: var(--background);
  position: relative;
}

/* Header do Chat */
.chat-header {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  background: var(--card);
  border-bottom: 1px solid var(--border);
  gap: 12px;
}

.back-btn {
  background: none;
  border: none;
  color: var(--foreground);
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  transition: background 0.2s;
}

.back-btn:hover {
  background: var(--background);
}

.chat-user-info {
  display: flex;
  align-items: center;
  gap: 12px;
  flex: 1;
}

.chat-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
}

.user-details {
  display: flex;
  flex-direction: column;
}

.user-name {
  margin: 0;
  font-size: 16px;
  font-weight: 600;
  color: var(--foreground);
}

.user-status {
  font-size: 12px;
  color: #8e8e8e;
}

.user-status.online {
  color: #44b883;
}

.chat-actions {
  display: flex;
  gap: 8px;
}

.action-btn {
  background: none;
  border: none;
  color: var(--foreground);
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  transition: background 0.2s;
}

.action-btn:hover {
  background: var(--background);
}

/* √Årea de mensagens */
.messages-area {
  flex: 1;
  overflow-y: auto;
  padding: 16px;
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.message {
  display: flex;
  justify-content: flex-start;
}

.message.my-message {
  justify-content: flex-end;
}

.message-bubble {
  max-width: 70%;
  padding: 12px 16px;
  border-radius: 18px;
  background: var(--card);
  border: 1px solid var(--border);
}

.my-message .message-bubble {
  background: #0095f6;
  color: white;
  border: none;
}

.message-text {
  margin: 0 0 4px 0;
  line-height: 1.4;
  word-wrap: break-word;
}

.message-info {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: 4px;
  margin-top: 4px;
}

.message-time {
  font-size: 11px;
  opacity: 0.7;
}

.my-message .message-time {
  color: rgba(255, 255, 255, 0.8);
}

.message-status {
  display: flex;
  align-items: center;
}

.message-status.read {
  color: #0095f6;
}

.sending-spinner {
  width: 12px;
  height: 12px;
  border: 2px solid rgba(255, 255, 255, 0.3);
  border-top-color: rgba(255, 255, 255, 0.8);
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  to { transform: rotate(360deg); }
}

/* Indicador de digita√ß√£o */
.typing-indicator {
  display: flex;
  justify-content: flex-start;
}

.typing-bubble {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 18px;
  padding: 12px 16px;
}

.typing-dots {
  display: flex;
  gap: 4px;
  align-items: center;
}

.typing-dots span {
  width: 6px;
  height: 6px;
  background: #8e8e8e;
  border-radius: 50%;
  animation: bounce 1.4s ease-in-out infinite both;
}

.typing-dots span:nth-child(1) { animation-delay: -0.32s; }
.typing-dots span:nth-child(2) { animation-delay: -0.16s; }

@keyframes bounce {
  0%, 80%, 100% { transform: scale(0); opacity: 0.5; }
  40% { transform: scale(1); opacity: 1; }
}

/* Input de mensagem */
.message-input-area {
  background: var(--card);
  border-top: 1px solid var(--border);
  padding: 12px 16px;
}

.input-container {
  display: flex;
  align-items: center;
  gap: 8px;
}

.attachment-btn {
  background: none;
  border: none;
  color: var(--foreground);
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  transition: background 0.2s;
}

.attachment-btn:hover {
  background: var(--background);
}

.message-input {
  flex: 1;
  border: 1px solid var(--border);
  border-radius: 20px;
  padding: 10px 16px;
  background: var(--background);
  color: var(--foreground);
  font-size: 14px;
  outline: none;
  resize: none;
}

.message-input:focus {
  border-color: #0095f6;
}

.send-btn {
  background: #e0e0e0;
  border: none;
  color: #8e8e8e;
  cursor: pointer;
  padding: 10px;
  border-radius: 50%;
  transition: all 0.2s;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.send-btn.active {
  background: #0095f6;
  color: white;
}

.send-btn:disabled {
  cursor: not-allowed;
}

/* Op√ß√µes de anexo */
.attachment-options {
  display: flex;
  gap: 8px;
  margin-top: 8px;
  padding: 8px;
  background: var(--background);
  border-radius: 12px;
}

.attachment-option {
  display: flex;
  align-items: center;
  gap: 6px;
  padding: 8px 12px;
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 8px;
  color: var(--foreground);
  font-size: 12px;
  cursor: pointer;
  transition: background 0.2s;
}

.attachment-option:hover {
  background: var(--background);
}

/* Painel de informa√ß√µes do usu√°rio */
.user-info-panel {
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  z-index: 1000;
  display: flex;
  justify-content: flex-end;
}

.user-info-content {
  width: 300px;
  height: 100%;
  background: var(--card);
  padding: 20px;
  overflow-y: auto;
}

.close-info-btn {
  background: none;
  border: none;
  color: var(--foreground);
  cursor: pointer;
  padding: 8px;
  border-radius: 50%;
  float: right;
  margin-bottom: 20px;
}

.user-profile {
  text-align: center;
  margin-bottom: 30px;
}

.profile-avatar {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  object-fit: cover;
  margin-bottom: 12px;
}

.user-profile h3 {
  margin: 0 0 8px 0;
  color: var(--foreground);
}

.user-profile p {
  margin: 4px 0;
  color: #8e8e8e;
  font-size: 14px;
}

.user-actions {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.action-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px;
  background: var(--background);
  border: 1px solid var(--border);
  border-radius: 8px;
  color: var(--foreground);
  font-size: 14px;
  cursor: pointer;
  transition: background 0.2s;
}

.action-item:hover {
  background: var(--card);
}

.action-item.danger {
  color: #e74c3c;
}

@media (max-width: 480px) {
  .user-info-content {
    width: 280px;
  }
  
  .message-bubble {
    max-width: 85%;
  }
}

sections.script:
import HomeScreen from './HomeScreen.vue'
import SearchScreen from './SearchScreen.vue'
import PublishScreen from './PublishScreen.vue'
import MessagesScreen from './MessagesScreen.vue'
import ProfileScreen from './ProfileScreen.vue'
import NotificationsScreen from './NotificationsScreen.vue'
import IconComponent from './IconComponent.vue'

export default {
  name: 'FeedPosts',
  props: {
    currentUser: {
      type: Object,
      default: () => null
    }
  },
  components: {
    HomeScreen,
    SearchScreen,
    PublishScreen,
    MessagesScreen,
    ProfileScreen,
    NotificationsScreen,
    IconComponent
  },
  data() {
    return {
      currentTab: 'home',
      previousTab: 'home', // Para rastrear a tela anterior
      posts: [
        {
          user: 'ana_silva',
          image: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80',
          caption: 'Curtindo o dia!'
        },
        {
          user: 'joao_p',
          image: 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80',
          caption: 'Vista incr√≠vel!'
        }
      ],
      notificacoesNaoLidas: 3, // Altere para testar
      notificacaoUrgente: false, // true para badge vermelho
      notificacaoPositiva: true, // true para badge verde
      notificacoes: [
        {
          id: 1,
          type: 'like',
          username: 'ana.silva',
          timestamp: new Date(Date.now() - 1000 * 60 * 15),
          read: false,
          preview: 'https://picsum.photos/50/50?random=10'
        },
        {
          id: 2,
          type: 'comment',
          username: 'carlos.pereira',
          timestamp: new Date(Date.now() - 1000 * 60 * 60),
          read: false,
          comment: 'Excelente trabalho!',
          preview: 'https://picsum.photos/50/50?random=11'
        },
        {
          id: 3,
          type: 'follow',
          username: 'lucia.ferreira',
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 3),
          read: true,
          following: false
        }
      ]
    }
  },
  methods: {
    goBack() {
      this.currentTab = this.previousTab
    }
  },
  watch: {
    currentTab(newTab, oldTab) {
      // Atualiza a aba anterior apenas se n√£o estivermos voltando das notifica√ß√µes
      if (newTab !== 'notifications' && oldTab !== 'notifications') {
        this.previousTab = oldTab
      }
    }
  }
}

sections.style:
.fade-enter-active, .fade-leave-active {
  transition: opacity 0.3s;
}
.fade-enter-from, .fade-leave-to {
  opacity: 0;
}
.feed-container {
  max-width: 400px;
  margin: 0 auto;
  background: var(--card);
  border: 1px solid var(--border);
  min-height: 100vh;
  box-sizing: border-box;
  position: relative;
  color: var(--foreground);
}
.feed-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid var(--border);
  background: var(--background);
}
.logo {
  font-family: 'Arial Black', Arial, sans-serif;
  font-size: 1.5em;
  color: var(--primary);
}
.notification-icon {
  margin-left: auto;
  cursor: pointer;
  color: var(--primary);
  transition: all 0.3s ease;
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 8px;
  border-radius: 12px;
}
.notification-icon:hover {
  color: var(--accent);
  background: var(--background);
  transform: scale(1.05);
}
.badge {
  position: absolute;
  top: -6px;
  right: -10px;
  min-width: 20px;
  height: 20px;
  padding: 0 6px;
  background: var(--accent);
  color: #fff;
  border-radius: 12px;
  font-size: 0.85em;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 2px solid var(--card);
  box-shadow: 0 1px 4px rgba(0,0,0,0.08);
  z-index: 10;
  transition: background 0.2s;
}
.badge.urgente {
  background: #e74c3c; /* vermelho */
}
.badge.positiva {
  background: var(--verde-energia); /* verde */
}
main {
  padding: 16px 0;
  padding-bottom: 70px; /* espa√ßo para a navbar */
}

main.no-navbar {
  padding-bottom: 0; /* remove espa√ßo quando navbar est√° escondida */
}

/* Navbar inferior */
.bottom-navbar {
  position: fixed;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  width: 100vw;
  max-width: 400px;
  margin: 0 auto;
  background: var(--card);
  border-top: 1px solid var(--border);
  display: flex;
  justify-content: space-around;
  align-items: center;
  height: 56px;
  z-index: 100;
}
.nav-btn {
  background: none;
  border: none;
  outline: none;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: var(--foreground);
  cursor: pointer;
  flex: 1;
  padding: 8px 4px;
  transition: all 0.3s ease;
  border-radius: 12px;
  position: relative;
}
.nav-btn:active, .nav-btn:focus {
  background: var(--background);
}
.nav-btn:hover {
  background: var(--background);
  transform: translateY(-2px);
}
.nav-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 4px;
  transition: all 0.3s ease;
}
.nav-label {
  font-size: 0.75em;
  font-weight: 500;
  letter-spacing: 0.5px;
}
.publish {
  color: var(--primary);
  font-weight: bold;
}

.bottom-navbar.hidden {
  display: none;
}

/* Responsividade */
@media (max-width: 500px) {
  .feed-container {
    max-width: 100vw;
    border: none;
    color: var(--foreground);
  }
  .feed-header {
    padding: 10px 6px;
  }
  main {
    padding: 8px 0;
    padding-bottom: 70px;
  }
  .bottom-navbar {
    max-width: 100vw;
  }
}

sections.script:
import PostItem from './PostItem.vue'

export default {
  name: 'HomeScreen',
  props: {
    posts: Array
  },
  components: {
    PostItem
  }
}

sections.style:
.list-enter-active, .list-leave-active {
  transition: all 0.4s;
}
.list-enter-from {
  opacity: 0;
