sections.script:
export default {
  name: 'LoginForm',
  data() {
    return {
      email: '',
      password: '',
      message: '',
      messageType: '',
      isLoading: false,
      errors: {
        email: '',
        password: ''
      }
    }
  },
  computed: {
    isFormValid() {
      return this.email && 
             this.password && 
             !this.errors.email && 
             !this.errors.password &&
             this.isInstitutionalEmail(this.email)
    }
  },
  methods: {
    isInstitutionalEmail(email) {
      const institutionalDomains = [
        '@estudante.ifmt.edu.br',
        '@professor.ifmt.edu.br',
        '@ifmt.edu.br'
      ]
      return institutionalDomains.some(domain => email.endsWith(domain))
    },

    validateEmail() {
      this.errors.email = ''
      
      if (!this.email) {
        this.errors.email = 'E-mail √© obrigat√≥rio'
        return false
      }
      
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
      if (!emailRegex.test(this.email)) {
        this.errors.email = 'E-mail inv√°lido'
        return false
      }
      
      if (!this.isInstitutionalEmail(this.email)) {
        this.errors.email = 'Use apenas e-mail institucional do IFMT'
        return false
      }
      
      return true
    },

    validatePassword() {
      this.errors.password = ''
      
      if (!this.password) {
        this.errors.password = 'Senha √© obrigat√≥ria'
        return false
      }
      
      if (this.password.length < 6) {
        this.errors.password = 'Senha deve ter pelo menos 6 caracteres'
        return false
      }
      
      return true
    },

    // Simula hash de senha (apenas para demonstra√ß√£o)
    hashPassword(password) {
      // Em produ√ß√£o, use uma biblioteca de hash real como bcrypt
      let hash = 0
      for (let i = 0; i < password.length; i++) {
        const char = password.charCodeAt(i)
        hash = ((hash << 5) - hash) + char
        hash = hash & hash // Converte para 32bit integer
      }
      return Math.abs(hash).toString(16)
    },

    // Simula verifica√ß√£o no banco de dados
    findUserByEmail(email) {
      const users = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
      return users.find(user => user.email === email)
    },

    // Verifica se a senha est√° correta
    verifyPassword(inputPassword, storedPasswordHash) {
      const inputHash = this.hashPassword(inputPassword)
      return inputHash === storedPasswordHash
    },

    async login() {
      // Reset de erros e mensagens
      this.errors = { email: '', password: '' }
      this.message = ''
      
      // Valida√ß√£o dos campos
      const emailValid = this.validateEmail()
      const passwordValid = this.validatePassword()
      
      if (!emailValid || !passwordValid) {
        return
      }
      
      this.isLoading = true
      
      try {
        // Simula delay de requisi√ß√£o
        await new Promise(resolve => setTimeout(resolve, 1500))
        
        // Verifica se o usu√°rio existe
        const user = this.findUserByEmail(this.email)
        
        if (!user) {
          this.message = 'E-mail n√£o encontrado. Verifique ou crie uma conta.'
          this.messageType = 'error'
          this.errors.email = 'Usu√°rio n√£o cadastrado'
          this.isLoading = false
          return
        }
        
        // Verifica a senha
        if (!this.verifyPassword(this.password, user.passwordHash)) {
          this.message = 'Senha incorreta. Tente novamente.'
          this.messageType = 'error'
          this.errors.password = 'Senha incorreta'
          this.isLoading = false
          return
        }
        
        // Login bem-sucedido
        this.message = `Bem-vindo(a), ${user.name}!`
        this.messageType = 'success'
        
        // Salva sess√£o do usu√°rio
        localStorage.setItem('ifwave_current_user', JSON.stringify({
          id: user.id,
          name: user.name,
          email: user.email,
          course: user.course,
          campus: user.campus,
          loginTime: new Date().toISOString()
        }))
        
        // Redireciona ap√≥s 1.5 segundos
        setTimeout(() => {
          this.$emit('logged-in', user)
        }, 1500)
        
      } catch (error) {
        this.message = 'Erro interno. Tente novamente.'
        this.messageType = 'error'
      } finally {
        this.isLoading = false
      }
    }
  }
}

