    margin: 16px;
    max-width: none;
  }
}

sections.script:
import IconComponent from './IconComponent.vue'
import ChatScreen from './ChatScreen.vue'

export default {
  name: 'MessagesScreen',
  components: {
    IconComponent,
    ChatScreen
  },
  props: {
    currentUser: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      searchQuery: '',
      showNewMessage: false,
      newMessageQuery: '',
      selectedChat: null,
      activeChats: [],
      availableUsers: []
    }
  },
  computed: {
    filteredChats() {
      if (!this.searchQuery) return this.activeChats
      return this.activeChats.filter(chat => 
        chat.name.toLowerCase().includes(this.searchQuery.toLowerCase()) ||
        chat.course.toLowerCase().includes(this.searchQuery.toLowerCase())
      )
    },
    
    filteredContacts() {
      let contacts = this.availableUsers.filter(user => {
        // Verificar se currentUser existe e tem id antes de comparar
        const currentUserId = this.currentUser && this.currentUser.id ? this.currentUser.id : null
        return currentUserId && user.id !== currentUserId && // N√£o mostrar o pr√≥prio usu√°rio
               !this.isBlocked(user.id) // N√£o mostrar usu√°rios bloqueados
      })
      
      if (this.newMessageQuery) {
        contacts = contacts.filter(contact =>
          contact.name.toLowerCase().includes(this.newMessageQuery.toLowerCase()) ||
          contact.course.toLowerCase().includes(this.newMessageQuery.toLowerCase()) ||
          contact.campus.toLowerCase().includes(this.newMessageQuery.toLowerCase())
        )
      }
      
      return contacts
    },
    
    hasAnyConversations() {
      return this.activeChats.length > 0
    }
  },
  mounted() {
    // Verificar se currentUser est√° dispon√≠vel antes de carregar dados
    if (this.currentUser && this.currentUser.id) {
      this.loadRegisteredUsers()
      this.loadActiveConversations()
    } else {
      // Se n√£o h√° usu√°rio logado, aguardar um pouco e tentar novamente
      setTimeout(() => {
        if (this.currentUser && this.currentUser.id) {
          this.loadRegisteredUsers()
          this.loadActiveConversations()
        }
      }, 500)
    }
  },
  methods: {
    loadRegisteredUsers() {
      // Carregar todos os usu√°rios cadastrados do localStorage
      const users = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
      
      this.availableUsers = users
        .filter(user => user && typeof user === 'object' && user.id) // Filtrar apenas objetos v√°lidos com id
        .map(user => ({
          ...user,
          online: Math.random() > 0.5, // Simular status online
          avatar: user.avatar || this.generateAvatar(user.name || 'Usu√°rio')
        }))
        .filter(user => {
          // Verificar se currentUser existe e tem id antes de comparar
          return this.currentUser && this.currentUser.id && user.id !== this.currentUser.id
        })
    },

    loadActiveConversations() {
      // Carregar conversas ativas do localStorage
      const conversations = JSON.parse(localStorage.getItem('ifwave_conversations') || '[]')
      
      this.activeChats = conversations.map(conv => {
        // Buscar dados atualizados do usu√°rio
        const user = this.availableUsers.find(u => u.id === conv.userId)
        if (user) {
          return {
            ...conv,
            name: user.name,
            course: user.course,
            campus: user.campus,
            avatar: user.avatar,
            online: user.online
          }
        }
        return conv
      }).filter(conv => conv && !this.isBlocked(conv.userId))
      
      // Ordenar por timestamp mais recente
      this.activeChats.sort((a, b) => (b.timestamp || 0) - (a.timestamp || 0))
    },

    generateAvatar(name) {
      const avatars = [
        'https://images.unsplash.com/photo-1494790108755-2616b612b750?auto=format&fit=crop&w=150&q=80',
        'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?auto=format&fit=crop&w=150&q=80',
        'https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=150&q=80',
        'https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&w=150&q=80',
        'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?auto=format&fit=crop&w=150&q=80',
        'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?auto=format&fit=crop&w=150&q=80'
      ]
      // Usar o hash do nome para escolher um avatar consistente
      const hash = name.split('').reduce((a, b) => {
        a = ((a << 5) - a) + b.charCodeAt(0)
        return a & a
      }, 0)
      return avatars[Math.abs(hash) % avatars.length]
    },

    isBlocked(userId) {
      const blockedUsers = JSON.parse(localStorage.getItem('ifwave_blocked_users') || '[]')
      return blockedUsers.includes(userId)
    },

    openChat(chat) {
      // Buscar dados completos do usu√°rio
      const user = this.availableUsers.find(u => u.id === chat.userId) || {
        id: chat.userId,
        name: chat.name,
        course: chat.course,
        campus: chat.campus || 'IFMT',
        avatar: chat.avatar,
        online: chat.online || false
      }
      
      this.selectedChat = user
      
      // Marcar conversa como lida
      this.markAsRead(chat.id)
    },

    startChatWith(user) {
      this.selectedChat = user
    },

    selectContact(contact) {
      this.showNewMessage = false
      this.newMessageQuery = ''
      
      // Verificar se j√° existe uma conversa com este usu√°rio
      const existingChat = this.activeChats.find(chat => chat.userId === contact.id)
      
      if (existingChat) {
        // Abrir conversa existente
        this.selectedChat = contact
        this.markAsRead(existingChat.id)
      } else {
        // Criar nova conversa
        this.createNewConversation(contact)
        this.selectedChat = contact
      }
    },

    createNewConversation(contact) {
      const conversations = JSON.parse(localStorage.getItem('ifwave_conversations') || '[]')
      const chatId = `chat_${this.currentUser.id}_${contact.id}`
      
      const newConversation = {
        id: chatId,
        userId: contact.id,
        name: contact.name,
        avatar: contact.avatar,
        course: contact.course,
        campus: contact.campus,
        lastMessage: '',
        lastMessageTime: '',
        timestamp: Date.now(),
        unread: false,
        unreadCount: 0,
        online: contact.online || false
      }
      
      conversations.unshift(newConversation) // Adicionar no in√≠cio
      localStorage.setItem('ifwave_conversations', JSON.stringify(conversations))
      this.loadActiveConversations()
    },

    markAsRead(chatId) {
      const conversations = JSON.parse(localStorage.getItem('ifwave_conversations') || '[]')
      const conv = conversations.find(c => c.id === chatId)
      if (conv) {
        conv.unread = false
        conv.unreadCount = 0
        localStorage.setItem('ifwave_conversations', JSON.stringify(conversations))
        this.loadActiveConversations()
      }
    },

    handleUserBlocked() {
      // Remover usu√°rio da lista e fechar chat
      this.selectedChat = null
      this.loadActiveConversations()
      this.loadRegisteredUsers()
    },

    handleUserStatusChange(data) {
      // Atualizar status online do usu√°rio
      const user = this.availableUsers.find(u => u.id === data.userId)
      if (user) {
        user.online = data.online
      }
      
      const chat = this.activeChats.find(c => c.userId === data.userId)
      if (chat) {
        chat.online = data.online
      }
    },

    viewProfile(user) {
      // Emitir evento para o componente pai navegar para o perfil
      this.$emit('view-profile', user)
    },

    backToMessages() {
      this.selectedChat = null
      // Recarregar conversas para mostrar atualiza√ß√µes
      this.loadActiveConversations()
    },

    handleMessageSent(data) {
      // Atualizar lista de conversas quando mensagem √© enviada
      setTimeout(() => {
        this.loadActiveConversations()
      }, 100)
      
      // Exibir notifica√ß√£o de sucesso (opcional)
      console.log('Mensagem enviada para:', data.recipient.name)
    },

    filterContacts() {
      // Este m√©todo √© chamado automaticamente pelo computed filteredContacts
      // Mas pode ser usado para a√ß√µes adicionais quando o usu√°rio digita
    },
  },
  
  watch: {
    // Atualizar conversas quando houver mudan√ßas
    '$route'() {
      this.loadActiveConversations()
    }
  }
}

sections.style:
.messages-container {
  background: var(--background);
  color: var(--foreground);
  min-height: 100vh;
  font-family: 'Roboto', 'Open Sans', Arial, sans-serif;
}

/* Header */
.messages-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 20px;
  border-bottom: 1px solid var(--border);
  background: var(--card);
}

.messages-title {
  font-size: 1.5em;
  font-weight: bold;
  color: var(--primary);
  margin: 0;
}

.header-actions {
  display: flex;
  gap: 12px;
}

.action-btn {
  background: none;
  border: none;
  padding: 8px;
  border-radius: 50%;
  cursor: pointer;
  color: var(--primary);
  transition: all 0.2s ease;
}

.action-btn:hover {
  background: var(--background);
  transform: scale(1.1);
}

/* Barra de pesquisa */
.search-bar {
  padding: 16px 20px;
  background: var(--card);
  border-bottom: 1px solid var(--border);
}

.search-input-container {
  position: relative;
  display: flex;
  align-items: center;
}

.search-icon {
  position: absolute;
  left: 12px;
  color: var(--foreground);
  opacity: 0.6;
  z-index: 1;
}

.search-input {
  width: 100%;
  padding: 12px 12px 12px 40px;
  border: 1px solid var(--border);
  border-radius: 24px;
  background: var(--background);
  color: var(--foreground);
  font-size: 14px;
  outline: none;
  transition: border-color 0.2s ease;
}

.search-input:focus {
  border-color: var(--primary);
}

/* Se√ß√µes */
.section-title {
  font-size: 1.1em;
  font-weight: 600;
  color: var(--primary);
  margin: 20px 20px 12px 20px;
  padding-bottom: 8px;
  border-bottom: 2px solid var(--verde-energia);
}

/* Lista de conversas */
.chat-list, .group-list, .teacher-list, .user-list {
  padding: 0 8px;
}

.chat-item, .group-item, .teacher-item, .user-item {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  margin: 4px 0;
  border-radius: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  background: var(--card);
}

.chat-item:hover, .group-item:hover, .teacher-item:hover, .user-item:hover {
  background: var(--background);
  transform: translateX(4px);
}

.chat-item.unread {
  background: linear-gradient(90deg, var(--card) 0%, rgba(46, 204, 113, 0.1) 100%);
  border-left: 3px solid var(--verde-energia);
}

/* Avatares */
.chat-avatar, .teacher-avatar, .user-avatar {
  position: relative;
  margin-right: 16px;
}

.chat-avatar img, .teacher-avatar img, .user-avatar img {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid var(--border);
}

.online-indicator {
  position: absolute;
  bottom: 2px;
  right: 2px;
  width: 12px;
  height: 12px;
  background: var(--verde-energia);
  border: 2px solid var(--card);
  border-radius: 50%;
}

.group-avatar {
  position: relative;
  margin-right: 16px;
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.group-icon {
  width: 48px;
  height: 48px;
  background: linear-gradient(135deg, var(--azul-ifmt), var(--roxo-inovador));
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
}

.group-notification {
  position: absolute;
  top: 0;
  right: 0;
  width: 12px;
  height: 12px;
  background: var(--amarelo-alegria);
  border: 2px solid var(--card);
  border-radius: 50%;
}

.teacher-status {
  position: absolute;
  bottom: 2px;
  right: 2px;
  width: 12px;
  height: 12px;
  border: 2px solid var(--card);
  border-radius: 50%;
}

.teacher-status.available {
  background: var(--verde-energia);
}

.teacher-status.busy {
  background: #e74c3c;
}

.teacher-status.away {
  background: var(--amarelo-alegria);
}

/* Informa√ß√µes */
.chat-info, .group-info, .teacher-info, .user-info {
  flex: 1;
  min-width: 0;
}

.chat-header, .group-header, .teacher-header, .user-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
}

.chat-name, .group-name, .teacher-name, .user-name {
  font-weight: 600;
  color: var(--foreground);
  font-size: 15px;
}

.chat-course, .group-members, .teacher-department, .user-course {
  font-size: 12px;
  color: var(--primary);
  font-weight: 500;
}

.chat-time, .last-activity, .office-hours {
  font-size: 12px;
  color: var(--foreground);
  opacity: 0.7;
}

.chat-preview, .group-preview, .teacher-details, .user-details {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.last-message, .group-subject, .teacher-subject, .user-campus {
  font-size: 14px;
  color: var(--foreground);
  opacity: 0.8;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 200px;
}

.unread-badge {
  background: var(--verde-energia);
  color: white;
  font-size: 12px;
  font-weight: bold;
  padding: 2px 8px;
  border-radius: 12px;
  min-width: 20px;
  text-align: center;
}

/* Estado vazio */
.empty-state {
  text-align: center;
  padding: 60px 20px;
  color: var(--foreground);
}

.empty-icon {
  color: var(--primary);
  opacity: 0.5;
  margin-bottom: 20px;
}

.empty-title {
  font-size: 1.3em;
  font-weight: 600;
  margin-bottom: 12px;
  color: var(--primary);
}

.empty-description {
  font-size: 14px;
  opacity: 0.7;
  margin-bottom: 30px;
  line-height: 1.5;
}

.start-chat-btn {
  background: linear-gradient(135deg, var(--azul-ifmt), var(--verde-energia));
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 24px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;
}

.start-chat-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(0, 61, 124, 0.3);
}

/* Modal de nova mensagem */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
}

.new-message-modal {
  background: var(--card);
  border-radius: 16px;
  width: 90%;
  max-width: 400px;
  max-height: 80vh;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid var(--border);
}

.modal-header h3 {
  display: flex;
  align-items: center;
  gap: 8px;
  margin: 0;
  color: var(--primary);
  font-size: 1.2em;
}

.close-btn {
  background: none;
  border: none;
  padding: 4px;
  cursor: pointer;
  color: var(--foreground);
  border-radius: 50%;
  transition: background 0.2s ease;
}

.close-btn:hover {
  background: var(--background);
}

.modal-content {
  padding: 20px;
  max-height: 400px;
  overflow-y: auto;
}

.contact-search {
  width: 100%;
  padding: 12px 16px;
  border: 1px solid var(--border);
  border-radius: 24px;
  background: var(--background);
  color: var(--foreground);
  font-size: 14px;
  outline: none;
  margin-bottom: 20px;
}

.contact-search:focus {
  border-color: var(--primary);
}

.contact-suggestions {
  margin-top: 8px;
}

.contact-suggestions h4 {
  display: flex;
  align-items: center;
  gap: 8px;
  color: var(--foreground);
  font-size: 0.9em;
  margin-bottom: 12px;
  padding: 0 4px;
}

.suggestion-list {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.suggestion-item {
  display: flex;
  align-items: center;
  padding: 12px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease;
  border: 1px solid transparent;
}

.suggestion-item:hover {
  background: var(--background-hover);
  border-color: var(--primary-light);
  transform: translateY(-1px);
}

.suggestion-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
  margin-right: 12px;
  border: 2px solid var(--border);
}

.suggestion-info {
  display: flex;
  flex-direction: column;
}

.suggestion-name {
  font-weight: 600;
  color: var(--foreground);
  font-size: 14px;
}

.suggestion-detail {
  font-size: 12px;
  color: var(--primary);
  opacity: 0.8;
}

.suggestion-campus {
  font-size: 11px;
  color: var(--foreground);
  opacity: 0.6;
}

.suggestion-actions {
  display: flex;
  align-items: center;
  gap: 8px;
  margin-left: auto;
}

.start-chat-btn-small {
  background: var(--primary);
  color: white;
  border: none;
  border-radius: 50%;
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s ease;
}

.start-chat-btn-small:hover {
  background: var(--primary-dark);
  transform: scale(1.1);
}

.no-results {
  text-align: center;
  padding: 40px 20px;
  color: var(--foreground-muted);
}

.no-results-icon {
  margin-bottom: 16px;
  opacity: 0.5;
}

.no-results p {
  margin-bottom: 4px;
  color: var(--foreground);
}

.no-results small {
  color: var(--foreground-muted);
  font-size: 0.8em;
}

/* Estado de boas-vindas */
.welcome-state {
  text-align: center;
  padding: 40px 20px;
  color: var(--foreground-muted);
}

.welcome-state .welcome-icon {
  margin-bottom: 16px;
  color: var(--primary);
}

.welcome-state h3 {
  color: var(--foreground);
  margin-bottom: 8px;
  font-size: 1.2em;
}

.welcome-state p {
  color: var(--foreground-muted);
  font-size: 0.9em;
  line-height: 1.5;
}

/* Responsividade */
@media (max-width: 500px) {
  .messages-header {
    padding: 12px 16px;
  }
  
  .search-bar {
    padding: 12px 16px;
  }
  
  .section-title {
    margin: 16px 16px 8px 16px;
    font-size: 1em;
  }
  
  .chat-item, .group-item, .teacher-item {
    padding: 10px 12px;
  }
  
  .chat-avatar img, .teacher-avatar img {
    width: 42px;
    height: 42px;
  }
  
  .group-icon {
    width: 42px;
    height: 42px;
  }
}

sections.script:
export default {
  name: 'NotificationsScreen',
  props: {
    notifications: {
      type: Array,
      default: () => []
    }
  },
  data() {
    return {
      activeTab: 'all',
      tabs: [
        { id: 'all', label: 'Todas' },
        { id: 'likes', label: 'Curtidas' },
        { id: 'comments', label: 'Coment√°rios' },
        { id: 'follows', label: 'Seguidores' }
      ],
      mockNotifications: [
        {
          id: 1,
          type: 'like',
          username: 'ana.silva',
          avatar: null,
          timestamp: new Date(Date.now() - 1000 * 60 * 30), // 30 min ago
          read: false,
          preview: 'https://picsum.photos/50/50?random=1'
        },
        {
          id: 2,
          type: 'comment',
          username: 'pedro.costa',
          avatar: null,
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 2), // 2h ago
          read: false,
          comment: 'Muito legal esse projeto!',
          preview: 'https://picsum.photos/50/50?random=2'
        },
        {
          id: 3,
          type: 'follow',
          username: 'maria.santos',
          avatar: null,
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 4), // 4h ago
          read: true,
          following: false
        },
        {
          id: 4,
          type: 'mention',
          username: 'joao.oliveira',
          avatar: null,
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24), // 1 day ago
          read: true,
          post: 'Novo projeto no GitHub!'
        },
        {
          id: 5,
          type: 'like',
          username: 'carla.lima',
          avatar: null,
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2), // 2 days ago
          read: true,
          preview: 'https://picsum.photos/50/50?random=3'
        },
        {
          id: 6,
          type: 'comment',
          username: 'rafael.souza',
          avatar: null,
          timestamp: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5), // 5 days ago
          read: true,
          comment: 'Parab√©ns pelo c√≥digo!',
          preview: 'https://picsum.photos/50/50?random=4'
        }
      ]
    }
  },
  computed: {
    allNotifications() {
      return [...this.mockNotifications, ...this.notifications].sort((a, b) => 
        new Date(b.timestamp) - new Date(a.timestamp)
      )
    },
    
    filteredNotifications() {
      let filtered = this.allNotifications

      if (this.activeTab !== 'all') {
        const typeMap = {
          'likes': ['like'],
          'comments': ['comment'],
          'follows': ['follow']
        }
        filtered = filtered.filter(n => typeMap[this.activeTab]?.includes(n.type))
      }

      return filtered
    },
    
    todayNotifications() {
      const today = new Date()
      today.setHours(0, 0, 0, 0)
      
      return this.filteredNotifications.filter(n => {
        const notifDate = new Date(n.timestamp)
        notifDate.setHours(0, 0, 0, 0)
        return notifDate.getTime() === today.getTime()
      })
    },
    
    thisWeekNotifications() {
      const today = new Date()
      const weekStart = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay())
      const yesterday = new Date(today.getFullYear(), today.getMonth(), today.getDate() - 1)
      
      return this.filteredNotifications.filter(n => {
        const notifDate = new Date(n.timestamp)
        return notifDate >= weekStart && notifDate <= yesterday
      })
    },
    
    olderNotifications() {
      const today = new Date()
      const weekStart = new Date(today.getFullYear(), today.getMonth(), today.getDate() - today.getDay())
      
      return this.filteredNotifications.filter(n => {
        const notifDate = new Date(n.timestamp)
        return notifDate < weekStart
      })
    }
  },
  methods: {
    getDefaultAvatar(username) {
      // Gera um avatar baseado no nome do usu√°rio
      const colors = ['FF6B6B', '4ECDC4', '45B7D1', 'FFA07A', '98D8C8', 'F7DC6F', 'BB8FCE', '85C1E9']
      const colorIndex = username.length % colors.length
      const initials = username.split('.').map(n => n[0]).join('').toUpperCase().slice(0, 2)
      
      return `https://ui-avatars.com/api/?name=${initials}&background=${colors[colorIndex]}&color=fff&size=44&format=svg`
    },
    
    getActionText(notification) {
      switch (notification.type) {
        case 'like':
          return 'curtiu sua publica√ß√£o.'
        case 'comment':
          return `comentou: "${notification.comment || 'Coment√°rio interessante!'}"`
        case 'follow':
          return 'come√ßou a seguir voc√™.'
        case 'mention':
          return `mencionou voc√™ em: "${notification.post || 'uma publica√ß√£o'}"`
        default:
          return 'interagiu com voc√™.'
      }
    },
    
    getRelativeTime(timestamp) {
      const now = new Date()
      const time = new Date(timestamp)
      const diffInSeconds = Math.floor((now - time) / 1000)
      
      if (diffInSeconds < 60) {
        return 'agora'
      } else if (diffInSeconds < 3600) {
        const minutes = Math.floor(diffInSeconds / 60)
        return `${minutes}min`
      } else if (diffInSeconds < 86400) {
        const hours = Math.floor(diffInSeconds / 3600)
        return `${hours}h`
      } else if (diffInSeconds < 604800) {
        const days = Math.floor(diffInSeconds / 86400)
        return `${days}d`
      } else {
        const weeks = Math.floor(diffInSeconds / 604800)
        return `${weeks}sem`
      }
    },
    
    markAsRead(notification) {
      notification.read = true
      // Aqui voc√™ pode fazer uma chamada para a API para marcar como lida
      this.$emit('notification-read', notification)
    },
    
    followUser(notification) {
      notification.following = true
      // Aqui voc√™ pode fazer uma chamada para a API para seguir o usu√°rio
      this.$emit('follow-user', notification.username)
    },
    
    unfollowUser(notification) {
      notification.following = false
      // Aqui voc√™ pode fazer uma chamada para a API para deixar de seguir
      this.$emit('unfollow-user', notification.username)
    }
  }
}

sections.style:
.notifications-container {
  min-height: 100vh;
  background: #ffffff;
  color: #262626;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;
}

/* Header */
.notifications-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 16px;
  height: 60px;
  background: #ffffff;
  border-bottom: 1px solid #dbdbdb;
  position: sticky;
  top: 0;
  z-index: 10;
}

.back-btn {
  background: none;
  border: none;
  padding: 8px;
  cursor: pointer;
  color: #262626;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  transition: background-color 0.2s;
}

.back-btn:hover {
  background-color: #f5f5f5;
}

.header-title {
  font-size: 16px;
  font-weight: 600;
  color: #262626;
  margin: 0;
}

.header-actions {
  display: flex;
  gap: 8px;
}

.action-btn {
  background: none;
  border: none;
  padding: 8px;
  cursor: pointer;
  color: #262626;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  transition: background-color 0.2s;
}

.action-btn:hover {
  background-color: #f5f5f5;
}

/* Content */
.notifications-content {
  padding-bottom: 80px;
}

/* Filter Tabs */
.filter-tabs {
  display: flex;
  background: #ffffff;
  border-bottom: 1px solid #dbdbdb;
  overflow-x: auto;
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.filter-tabs::-webkit-scrollbar {
  display: none;
}

.tab-btn {
  background: none;
  border: none;
  padding: 16px 20px;
  font-size: 14px;
  font-weight: 500;
  color: #8e8e8e;
  cursor: pointer;
  border-bottom: 2px solid transparent;
  transition: all 0.2s;
  white-space: nowrap;
  min-width: max-content;
}

.tab-btn.active {
  color: #262626;
  border-bottom-color: #262626;
}

.tab-btn:hover {
  color: #262626;
}

/* Notifications List */
.notifications-list {
  background: #ffffff;
}

.time-section {
  margin-bottom: 24px;
}

.section-title {
  padding: 16px 16px 8px 16px;
  margin: 0;
  font-size: 16px;
  font-weight: 600;
  color: #262626;
}

.notifications-group {
  background: #ffffff;
}

.notification-item {
  display: flex;
  align-items: flex-start;
  padding: 12px 16px;
  position: relative;
  cursor: pointer;
  transition: background-color 0.2s;
  border-bottom: 1px solid #efefef;
}

.notification-item:hover {
  background-color: #fafafa;
}

.notification-item.unread {
  background-color: #f8f9fa;
}

.notification-item:last-child {
  border-bottom: none;
}

/* Avatar */
.notification-avatar {
  width: 44px;
  height: 44px;
  margin-right: 12px;
  position: relative;
  flex-shrink: 0;
}

.user-avatar {
  width: 44px;
  height: 44px;
  border-radius: 50%;
  object-fit: cover;
  border: 1px solid #dbdbdb;
}

.action-icon {
  width: 44px;
  height: 44px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
}

.like-icon {
  background: linear-gradient(45deg, #ff6b6b, #ee5a24);
  color: white;
}

.comment-icon {
  background: linear-gradient(45deg, #0095f6, #005bb5);
  color: white;
}

.follow-icon {
  background: linear-gradient(45deg, #833ab4, #6a1b9a);
  color: white;
}

.mention-icon {
  background: linear-gradient(45deg, #f39c12, #e67e22);
  color: white;
}

/* Content */
.notification-content {
  flex: 1;
  min-width: 0;
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
}

.notification-text {
  flex: 1;
  min-width: 0;
}

.username {
  font-weight: 600;
  color: #262626;
  margin-right: 4px;
}

.action-text {
  color: #262626;
  font-size: 14px;
  line-height: 18px;
  margin-right: 4px;
}

.time {
  color: #8e8e8e;
  font-size: 12px;
  white-space: nowrap;
  display: block;
  margin-top: 2px;
}

/* Preview */
.notification-preview {
  margin-left: 12px;
  flex-shrink: 0;
}

.preview-image {
  width: 44px;
  height: 44px;
  border-radius: 8px;
  object-fit: cover;
  border: 1px solid #dbdbdb;
}

/* Actions */
.notification-action {
  margin-left: 12px;
  flex-shrink: 0;
}

.follow-btn {
  background: #0095f6;
  color: white;
  border: none;
  padding: 6px 16px;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;
}

.follow-btn:hover {
  background: #1877f2;
}

.following-btn {
  background: #ffffff;
  color: #262626;
  border: 1px solid #dbdbdb;
  padding: 6px 16px;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.following-btn:hover {
  background: #f5f5f5;
}

/* Unread Indicator */
.unread-indicator {
  position: absolute;
  right: 16px;
  top: 50%;
  transform: translateY(-50%);
  width: 8px;
  height: 8px;
  background: #0095f6;
  border-radius: 50%;
}

/* Empty State */
.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 64px 32px;
  text-align: center;
  min-height: 50vh;
}

.empty-icon {
  color: #c7c7c7;
  margin-bottom: 16px;
}

.empty-title {
  font-size: 22px;
  font-weight: 300;
  color: #262626;
  margin: 0 0 8px 0;
}

.empty-description {
  font-size: 14px;
  color: #8e8e8e;
  margin: 0;
  line-height: 18px;
}

/* Mobile Responsive */
@media (max-width: 768px) {
  .notifications-header {
    padding: 0 12px;
    height: 56px;
  }
  
  .header-title {
    font-size: 18px;
  }
  
  .tab-btn {
    padding: 12px 16px;
    font-size: 13px;
  }
  
  .notification-item {
    padding: 10px 12px;
  }
  
  .notification-avatar {
    width: 40px;
    height: 40px;
    margin-right: 10px;
  }
  
  .user-avatar,
  .action-icon {
    width: 40px;
    height: 40px;
  }
  
  .preview-image {
    width: 40px;
    height: 40px;
  }
  
  .section-title {
    padding: 12px 12px 6px 12px;
    font-size: 15px;
  }
  
  .action-text {
    font-size: 13px;
  }
  
  .follow-btn,
  .following-btn {
    padding: 5px 12px;
    font-size: 13px;
  }
}

/* Dark Mode Support (opcional) */
@media (prefers-color-scheme: dark) {
  .notifications-container {
    background: #000000;
    color: #ffffff;
  }
  
  .notifications-header {
    background: #000000;
    border-bottom-color: #262626;
  }
  
  .back-btn,
  .action-btn,
  .header-title {
    color: #ffffff;
  }
  
  .back-btn:hover,
  .action-btn:hover {
    background-color: #1a1a1a;
  }
  
  .filter-tabs {
    background: #000000;
    border-bottom-color: #262626;
  }
  
  .tab-btn {
    color: #a8a8a8;
  }
  
  .tab-btn.active,
  .tab-btn:hover {
    color: #ffffff;
    border-bottom-color: #ffffff;
  }
  
  .notifications-list,
  .notifications-group {
    background: #000000;
  }
  
  .notification-item {
    border-bottom-color: #262626;
  }
  
  .notification-item:hover {
    background-color: #1a1a1a;
  }
  
  .notification-item.unread {
    background-color: #0d1117;
  }
  
  .user-avatar {
    border-color: #262626;
  }
  
  .username,
  .action-text,
  .section-title {
    color: #ffffff;
  }
  
  .time {
    color: #a8a8a8;
  }
  
  .preview-image {
    border-color: #262626;
  }
  
  .following-btn {
    background: #000000;
    color: #ffffff;
    border-color: #262626;
  }
  
  .following-btn:hover {
    background: #1a1a1a;
  }
  
  .empty-icon {
    color: #525252;
  }
  
  .empty-title {
    color: #ffffff;
  }
  
  .empty-description {
    color: #a8a8a8;
  }
}

