sections.script:
export default {
  name: 'RegisterForm',
  data() {
    return {
      name: '',
      email: '',
      course: '',
      campus: '',
      password: '',
      confirmPassword: '',
      message: '',
      messageType: 'success', // 'success' ou 'error'
      isLoading: false,
      errors: {}
    }
  },
  methods: {
    async register() {
      this.clearErrors()
      
      if (!this.validateForm()) {
        return
      }
      
      this.isLoading = true
      
      try {
        // Simula delay de API
        await new Promise(resolve => setTimeout(resolve, 1500))
        
        // Verifica se o usu√°rio j√° existe
        if (this.userExists(this.email)) {
          this.showError('Este e-mail j√° est√° cadastrado!')
          return
        }
        
        // Cadastra o usu√°rio
        this.saveUser()
        this.showSuccess(`Usu√°rio ${this.name} cadastrado com sucesso!`)
        
        // Emitir evento com dados do usu√°rio para login autom√°tico
        setTimeout(() => {
          const newUser = {
            id: Date.now().toString(),
            name: this.name,
            email: this.email,
            course: this.course,
            campus: this.campus
          }
          this.$emit('registered', newUser)
        }, 1500)
        
      } catch (error) {
        this.showError('Erro interno. Tente novamente.')
      } finally {
        this.isLoading = false
      }
    },
    
    validateForm() {
      let isValid = true
      
      // Valida√ß√£o do nome
      if (this.name.length < 3) {
        this.errors.name = 'Nome deve ter pelo menos 3 caracteres'
        isValid = false
      }
      
      // Valida√ß√£o do e-mail
      if (!this.isValidEmail(this.email)) {
        this.errors.email = 'E-mail inv√°lido'
        isValid = false
      } else if (!this.isInstitutionalEmail(this.email)) {
        this.errors.email = 'Use seu e-mail institucional (@estudante.ifmt.edu.br)'
        isValid = false
      }
      
      // Valida√ß√£o do curso
      if (this.course.length < 3) {
        this.errors.course = 'Informe seu curso'
        isValid = false
      }
      
      // Valida√ß√£o do campus
      if (this.campus.length < 3) {
        this.errors.campus = 'Informe seu campus'
        isValid = false
      }
      
      // Valida√ß√£o da senha
      if (this.password.length < 6) {
        this.errors.password = 'Senha deve ter pelo menos 6 caracteres'
        isValid = false
      }
      
      // Valida√ß√£o da confirma√ß√£o de senha
      if (this.password !== this.confirmPassword) {
        this.errors.confirmPassword = 'Senhas n√£o coincidem'
        isValid = false
      }
      
      return isValid
    },
    
    isValidEmail(email) {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
      return emailRegex.test(email)
    },
    
    isInstitutionalEmail(email) {
      const institutionalDomains = [
        '@estudante.ifmt.edu.br',
        '@professor.ifmt.edu.br',
        '@ifmt.edu.br'
      ]
      return institutionalDomains.some(domain => email.endsWith(domain))
    },
    
    userExists(email) {
      const users = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
      return users.some(user => user.email === email)
    },
    
    saveUser() {
      try {
        const users = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
        const newUser = {
          id: Date.now().toString(),
          name: this.name,
          email: this.email,
          course: this.course,
          campus: this.campus,
          passwordHash: this.hashPassword(this.password), // Compat√≠vel com LoginForm
          createdAt: new Date().toISOString()
        }
        
        users.push(newUser)
        
        // Salvar com verifica√ß√£o
        localStorage.setItem('ifwave_users', JSON.stringify(users))
        
        // Verificar se foi salvo corretamente
        const savedUsers = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
        const userWasSaved = savedUsers.find(u => u.email === this.email)
        
        if (!userWasSaved) {
          throw new Error('Falha ao salvar usu√°rio')
        }
        
        // Logar automaticamente o usu√°rio ap√≥s cadastro bem-sucedido
        const currentUser = {
          id: newUser.id,
          name: newUser.name,
          email: newUser.email,
          course: newUser.course,
          campus: newUser.campus,
          loginTime: new Date().toISOString()
        }
        
        localStorage.setItem('ifwave_current_user', JSON.stringify(currentUser))
        
        console.log('Usu√°rio cadastrado e logado automaticamente:', newUser.email)
        
      } catch (error) {
        console.error('Erro ao salvar usu√°rio:', error)
        throw error
      }
    },
    
    hashPassword(password) {
      // Mesmo sistema de hash usado no LoginForm para compatibilidade
      let hash = 0
      for (let i = 0; i < password.length; i++) {
        const char = password.charCodeAt(i)
        hash = ((hash << 5) - hash) + char
        hash = hash & hash // Converte para 32bit integer
      }
      return Math.abs(hash).toString(16)
    },
    
    clearErrors() {
      this.errors = {}
      this.message = ''
    },
    
    showSuccess(msg) {
      this.message = msg
      this.messageType = 'success'
    },
    
    showError(msg) {
      this.message = msg
      this.messageType = 'error'
    }
  }
}

