  color: #333;
  font-size: 0.9em;
}

.report-content small {
  color: #666;
  font-size: 0.8em;
}

.report-date {
  font-size: 0.8em;
  color: #666;
}

/* System Status */
.system-status {
  padding: 20px;
}

.status-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 12px 0;
  border-bottom: 1px solid #f8f9fa;
}

.status-item:last-child {
  border-bottom: none;
}

.status-label {
  color: #666;
  font-size: 0.9em;
}

.status-value {
  font-weight: bold;
  color: #333;
}

/* Quick Actions */
.quick-actions {
  padding: 20px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 12px;
}

.action-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  background: #f8f9fa;
  border: 1px solid #e9ecef;
  border-radius: 8px;
  color: #495057;
  font-size: 0.9em;
  cursor: pointer;
  transition: all 0.2s;
}

.action-btn:hover {
  background: #e9ecef;
  border-color: #007bff;
  color: #007bff;
}

/* Responsive */
@media (max-width: 768px) {
  .stats-grid {
    grid-template-columns: 1fr;
  }
  
  .dashboard-grid {
    grid-template-columns: 1fr;
  }
  
  .stat-card {
    padding: 16px;
  }
  
  .quick-actions {
    grid-template-columns: 1fr;
  }
}

sections.script:
import IconComponent from '../../components/IconComponent.vue'

export default {
  name: 'AdminModelView',
  components: {
    IconComponent
  },
  props: {
    modelName: {
      type: String,
      required: true
    },
    model: {
      type: Object,
      required: true
    },
    dataService: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      // View settings
      viewMode: 'table', // 'table' or 'cards'
      searchQuery: '',
      filters: {},
      sortField: '',
      sortDirection: 'desc',
      
      // Pagination
      currentPage: 1,
      itemsPerPage: 20,
      
      // Selection
      selectedItems: [],
      
      // Form
      showForm: false,
      editingItem: null,
      formData: {},
      jsonFields: {},
      fieldErrors: {},
      isSaving: false
    }
  },
  computed: {
    allItems() {
      return this.dataService.getAll(this.modelName)
    },
    
    filteredItems() {
      let items = [...this.allItems]
      
      // Apply search
      if (this.searchQuery) {
        const query = this.searchQuery.toLowerCase()
        items = items.filter(item => {
          return this.model.searchFields.some(field => {
            const value = item[field]
            return value && value.toString().toLowerCase().includes(query)
          })
        })
      }
      
      // Apply filters
      for (const [field, value] of Object.entries(this.filters)) {
        if (value) {
          items = items.filter(item => item[field] === value)
        }
      }
      
      return items
    },
    
    sortedItems() {
      if (!this.sortField) {
        return this.filteredItems
      }
      
      return [...this.filteredItems].sort((a, b) => {
        const aVal = a[this.sortField]
        const bVal = b[this.sortField]
        
        let comparison = 0
        if (aVal < bVal) comparison = -1
        else if (aVal > bVal) comparison = 1
        
        return this.sortDirection === 'asc' ? comparison : -comparison
      })
    },
    
    paginatedItems() {
      const start = (this.currentPage - 1) * this.itemsPerPage
      const end = start + this.itemsPerPage
      return this.sortedItems.slice(start, end)
    },
    
    totalPages() {
      return Math.ceil(this.sortedItems.length / this.itemsPerPage)
    },
    
    allSelected() {
      return this.paginatedItems.length > 0 && 
             this.paginatedItems.every(item => this.selectedItems.includes(item.id))
    },
    
    someSelected() {
      return this.selectedItems.length > 0 && !this.allSelected
    }
  },
  watch: {
    searchQuery() {
      this.currentPage = 1
    },
    filters: {
      handler() {
        this.currentPage = 1
      },
      deep: true
    }
  },
  mounted() {
    this.initializeFilters()
    this.applySorting()
  },
  methods: {
    initializeFilters() {
      this.model.listFilter?.forEach(field => {
        this.$set(this.filters, field, '')
      })
    },
    
    applySorting() {
      if (this.model.ordering && this.model.ordering.length > 0) {
        const firstOrder = this.model.ordering[0]
        if (firstOrder.startsWith('-')) {
          this.sortField = firstOrder.substring(1)
          this.sortDirection = 'desc'
        } else {
          this.sortField = firstOrder
          this.sortDirection = 'asc'
        }
      }
    },
    
    getFieldLabel(fieldName) {
      return this.model.fields[fieldName]?.label || fieldName
    },
    
    getFilterOptions(fieldName) {
      const field = this.model.fields[fieldName]
      
      if (field.type === 'select') {
        return field.options.map(option => ({
          value: option,
          label: option
        }))
      }
      
      if (field.type === 'boolean') {
        return [
          { value: true, label: 'Sim' },
          { value: false, label: 'N√£o' }
        ]
      }
      
      // Extract unique values from data
      const values = [...new Set(this.allItems.map(item => item[fieldName]))]
        .filter(val => val !== null && val !== undefined && val !== '')
      
      return values.map(value => ({
        value,
        label: this.formatFieldValue(value, fieldName)
      }))
    },
    
    getForeignKeyOptions(modelName) {
      const relatedItems = this.dataService.getAll(modelName)
      return relatedItems.map(item => ({
        value: item.id,
        label: item.name || item.title || item.email || item.id
      }))
    },
    
    formatFieldValue(value, fieldName) {
      if (value === null || value === undefined) return ''
      
      const field = this.model.fields[fieldName]
      
      if (field?.type === 'boolean') {
        return value ? 'Sim' : 'N√£o'
      }
      
      if (field?.type === 'datetime' || field?.type === 'date') {
        try {
          const date = new Date(value)
          return date.toLocaleDateString('pt-BR', {
            day: '2-digit',
            month: '2-digit',
            year: 'numeric',
            ...(field.type === 'datetime' && {
              hour: '2-digit',
              minute: '2-digit'
            })
          })
        } catch {
          return value
        }
      }
      
      if (field?.type === 'foreignkey') {
        const relatedItem = this.dataService.getById(field.model, value)
        return relatedItem ? (relatedItem.name || relatedItem.title || relatedItem.email || value) : value
      }
      
      if (typeof value === 'string' && value.length > 50) {
        return value.substring(0, 50) + '...'
      }
      
      return value
    },
    
    sortBy(field) {
      if (this.sortField === field) {
        this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc'
      } else {
        this.sortField = field
        this.sortDirection = 'asc'
      }
    },
    
    toggleSelectAll() {
      if (this.allSelected) {
        this.selectedItems = this.selectedItems.filter(id => 
          !this.paginatedItems.some(item => item.id === id)
        )
      } else {
        const newSelections = this.paginatedItems.map(item => item.id)
        this.selectedItems = [...new Set([...this.selectedItems, ...newSelections])]
      }
    },
    
    editItem(item) {
      this.editingItem = item
      this.formData = { ...item }
      
      // Handle JSON fields
      for (const [fieldName, fieldConfig] of Object.entries(this.model.fields)) {
        if (fieldConfig.type === 'json') {
          this.jsonFields[fieldName] = JSON.stringify(item[fieldName] || {}, null, 2)
        }
      }
      
      this.showForm = true
    },
    
    deleteItem(item) {
      if (confirm(`Tem certeza que deseja excluir este ${this.model.name.slice(0, -1).toLowerCase()}?`)) {
        try {
          this.dataService.delete(this.modelName, item.id)
          this.$forceUpdate()
        } catch (error) {
          alert('Erro ao excluir: ' + error.message)
        }
      }
    },
    
    bulkDelete() {
      if (confirm(`Tem certeza que deseja excluir ${this.selectedItems.length} item(s)?`)) {
        try {
          this.selectedItems.forEach(id => {
            this.dataService.delete(this.modelName, id)
          })
          this.selectedItems = []
          this.$forceUpdate()
        } catch (error) {
          alert('Erro ao excluir itens: ' + error.message)
        }
      }
    },
    
    closeForm() {
      this.showForm = false
      this.editingItem = null
      this.formData = {}
      this.jsonFields = {}
      this.fieldErrors = {}
      this.isSaving = false
    },
    
    async saveItem() {
      this.isSaving = true
      this.fieldErrors = {}
      
      try {
        // Validate and prepare data
        const data = { ...this.formData }
        
        // Handle JSON fields
        for (const [fieldName, value] of Object.entries(this.jsonFields)) {
          try {
            data[fieldName] = JSON.parse(value || '{}')
          } catch (error) {
            this.fieldErrors[fieldName] = 'JSON inv√°lido'
          }
        }
        
        // Check for validation errors
        if (Object.keys(this.fieldErrors).length > 0) {
          this.isSaving = false
          return
        }
        
        // Hash password if provided
        for (const [fieldName, fieldConfig] of Object.entries(this.model.fields)) {
          if (fieldConfig.type === 'password' && data[fieldName]) {
            data[fieldName] = this.hashPassword(data[fieldName])
          }
        }
        
        // Save data
        if (this.editingItem) {
          await this.dataService.update(this.modelName, this.editingItem.id, data)
        } else {
          await this.dataService.create(this.modelName, data)
        }
        
        this.closeForm()
        this.$forceUpdate()
      } catch (error) {
        alert('Erro ao salvar: ' + error.message)
      } finally {
        this.isSaving = false
      }
    },
    
    hashPassword(password) {
      let hash = 0
      for (let i = 0; i < password.length; i++) {
        const char = password.charCodeAt(i)
        hash = ((hash << 5) - hash) + char
        hash = hash & hash
      }
      return Math.abs(hash).toString(16)
    }
  }
}

sections.style:
.admin-model-view {
  padding: 0;
}

/* Header */
.model-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
  padding-bottom: 16px;
  border-bottom: 1px solid #e9ecef;
}

.header-left {
  display: flex;
  align-items: center;
  gap: 16px;
}

.back-btn {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 16px;
  background: #f8f9fa;
  border: 1px solid #dee2e6;
  border-radius: 6px;
  color: #495057;
  cursor: pointer;
  font-size: 14px;
  transition: all 0.2s;
}

.back-btn:hover {
  background: #e9ecef;
}

.model-header h2 {
  margin: 0;
  display: flex;
  align-items: center;
  gap: 12px;
  color: #333;
  font-size: 1.5em;
}

/* Filters */
.model-filters {
  display: flex;
  gap: 16px;
  align-items: center;
  margin-bottom: 24px;
  flex-wrap: wrap;
}

.search-bar {
  display: flex;
  align-items: center;
  gap: 8px;
  background: white;
  border: 1px solid #dee2e6;
  border-radius: 6px;
  padding: 0 12px;
  min-width: 300px;
}

.search-input {
  border: none;
  outline: none;
  padding: 10px 0;
  flex: 1;
  font-size: 14px;
}

.filters {
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
}

.filter-select {
  padding: 8px 12px;
  border: 1px solid #dee2e6;
  border-radius: 6px;
  background: white;
  font-size: 14px;
  min-width: 150px;
}

.view-controls {
  display: flex;
  gap: 4px;
  margin-left: auto;
}

.view-btn {
  padding: 8px;
  background: #f8f9fa;
  border: 1px solid #dee2e6;
  border-radius: 4px;
  cursor: pointer;
  color: #6c757d;
  transition: all 0.2s;
}

.view-btn:hover,
.view-btn.active {
  background: #007bff;
  color: white;
  border-color: #007bff;
}

/* Table View */
.table-wrapper {
  background: white;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.data-table {
  width: 100%;
  border-collapse: collapse;
}

.data-table th,
.data-table td {
  padding: 12px;
  text-align: left;
  border-bottom: 1px solid #e9ecef;
}

.data-table th {
  background: #f8f9fa;
  font-weight: 600;
  color: #495057;
  font-size: 14px;
}

.data-table th.sortable {
  cursor: pointer;
  user-select: none;
  position: relative;
}

.data-table th.sortable:hover {
  background: #e9ecef;
}

.data-table th.active {
  background: #007bff;
  color: white;
}

.sort-indicator {
  margin-left: 8px;
  font-size: 12px;
}

.data-row:hover {
  background: #f8f9fa;
}

.data-cell {
  font-size: 14px;
  color: #495057;
  max-width: 200px;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.actions-cell {
  white-space: nowrap;
}

.action-btn {
  padding: 6px;
  margin: 0 2px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.2s;
}

.action-btn.edit {
  background: #007bff;
  color: white;
}

.action-btn.edit:hover {
  background: #0056b3;
}

.action-btn.delete {
  background: #dc3545;
  color: white;
}

.action-btn.delete:hover {
  background: #c82333;
}

/* Cards View */
.cards-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
}

.item-card {
  background: white;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  overflow: hidden;
}

.card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px;
  background: #f8f9fa;
  border-bottom: 1px solid #e9ecef;
}

.card-actions {
  display: flex;
  gap: 8px;
}

.card-content {
  padding: 16px;
}

.card-field {
  display: flex;
  justify-content: space-between;
  margin-bottom: 12px;
  font-size: 14px;
}

.card-field:last-child {
  margin-bottom: 0;
}

.card-field strong {
  color: #495057;
  margin-right: 12px;
}

.card-field span {
  color: #6c757d;
  text-align: right;
  flex: 1;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
  margin-top: 24px;
}

.page-btn {
  padding: 8px 12px;
  background: white;
  border: 1px solid #dee2e6;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  color: #495057;
  transition: all 0.2s;
}

.page-btn:hover:not(:disabled) {
  background: #e9ecef;
}

.page-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.page-info {
  margin: 0 16px;
  font-size: 14px;
  color: #6c757d;
}

/* Bulk Actions */
.bulk-actions {
  position: fixed;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  background: white;
  border-radius: 8px;
  box-shadow: 0 4px 16px rgba(0,0,0,0.15);
  padding: 16px 24px;
  display: flex;
  align-items: center;
  gap: 16px;
  z-index: 1000;
}

.bulk-info {
  font-size: 14px;
  color: #495057;
  font-weight: 500;
}

.bulk-buttons {
  display: flex;
  gap: 8px;
}

/* Modal */
.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0,0,0,0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1001;
  padding: 20px;
}

.modal-content {
  background: white;
  border-radius: 12px;
  max-width: 600px;
  width: 100%;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
}

.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid #e9ecef;
}

.modal-header h3 {
  margin: 0;
  color: #333;
}

.close-btn {
  padding: 8px;
  background: none;
  border: none;
  cursor: pointer;
  color: #6c757d;
  border-radius: 4px;
  transition: all 0.2s;
}

.close-btn:hover {
  background: #f8f9fa;
  color: #495057;
}

/* Form */
.item-form {
  padding: 20px;
}

.form-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  margin-bottom: 24px;
}

.form-group.full-width {
  grid-column: 1 / -1;
}

.form-group label {
  display: block;
  margin-bottom: 6px;
  font-weight: 500;
  color: #495057;
  font-size: 14px;
}

.required {
  color: #dc3545;
}

.form-control {
  width: 100%;
  padding: 10px 12px;
  border: 1px solid #dee2e6;
  border-radius: 6px;
  font-size: 14px;
  transition: border-color 0.2s;
  box-sizing: border-box;
}

.form-control:focus {
  outline: none;
  border-color: #007bff;
  box-shadow: 0 0 0 2px rgba(0,123,255,0.25);
}

.form-control:readonly {
  background: #f8f9fa;
  color: #6c757d;
}

.checkbox-wrapper {
  display: flex;
  align-items: center;
  gap: 8px;
}

.form-checkbox {
  width: auto;
}

.checkbox-label {
  margin: 0;
  cursor: pointer;
}

.json-field {
  font-family: 'Courier New', monospace;
  font-size: 12px;
}

.field-error {
  color: #dc3545;
  font-size: 12px;
  margin-top: 4px;
}

.form-actions {
  display: flex;
  gap: 12px;
  justify-content: flex-end;
  padding-top: 16px;
  border-top: 1px solid #e9ecef;
}

.btn {
  padding: 10px 20px;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  display: inline-flex;
  align-items: center;
  gap: 8px;
}

.btn-primary {
  background: #007bff;
  color: white;
}

.btn-primary:hover:not(:disabled) {
  background: #0056b3;
}

.btn-primary:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.btn-secondary {
  background: #6c757d;
  color: white;
}

.btn-secondary:hover {
  background: #5a6268;
}

.btn-danger {
  background: #dc3545;
  color: white;
}

.btn-danger:hover {
  background: #c82333;
}

/* Responsive */
@media (max-width: 768px) {
  .model-filters {
    flex-direction: column;
    align-items: stretch;
  }
  
  .search-bar {
    min-width: auto;
  }
  
  .view-controls {
    margin-left: 0;
    justify-content: center;
  }
  
  .cards-grid {
    grid-template-columns: 1fr;
  }
  
  .form-grid {
    grid-template-columns: 1fr;
  }
  
  .form-actions {
    flex-direction: column;
  }
  
  .bulk-actions {
    left: 20px;
    right: 20px;
    transform: none;
    flex-direction: column;
    text-align: center;
  }
  
  .modal-content {
    margin: 10px;
    max-height: calc(100vh - 20px);
  }
  
  .data-table {
    font-size: 12px;
  }
  
  .data-table th,
  .data-table td {
    padding: 8px;
  }
}

sections.script:
import { models, adminConfig } from '../models/index.js'
import { adminDataService } from '../services/dataService.js'
import { adminAuthService } from '../services/authService.js'
import { setupAdmin } from '../utils/setupAdmin.js'
import IconComponent from '../../components/IconComponent.vue'
import AdminDashboard from './AdminDashboard.vue'
import AdminModelView from './AdminModelView.vue'
import AdminTools from './AdminTools.vue'

export default {
  name: 'AdminPanel',
  components: {
    IconComponent,
    AdminDashboard,
    AdminModelView,
    AdminTools
  },
  data() {
    return {
      models,
      config: adminConfig,
      dataService: adminDataService,
      authService: adminAuthService,
      isAuthenticated: false,
      session: null,
      currentView: 'dashboard', // dashboard, model, tools
      currentModel: null,
      
      // Login
      loginData: {
        username: '',
        password: ''
      },
      isLoggingIn: false,
      loginError: ''
    }
  },
  mounted() {
    this.initializeAdmin()
    this.checkAuth()
  },
  methods: {
    initializeAdmin() {
      // Verificar se √© a primeira execu√ß√£o e configurar dados
      const users = this.dataService.getAll('users')
      if (users.length === 0) {
        console.log('Primeira execu√ß√£o detectada. Configurando dados iniciais...')
        setupAdmin()
      }
    },

    checkAuth() {
      this.isAuthenticated = this.authService.isAuthenticated()
      if (this.isAuthenticated) {
        this.session = this.authService.getSession()
      }
    },

    async handleLogin() {
      this.isLoggingIn = true
      this.loginError = ''
      
      try {
        await new Promise(resolve => setTimeout(resolve, 1000)) // Simular delay
        
        const result = this.authService.login(this.loginData.username, this.loginData.password)
        
        if (result.success) {
          this.isAuthenticated = true
          this.session = result.session
          this.loginData = { username: '', password: '' }
        } else {
          this.loginError = result.error
        }
      } catch (error) {
        this.loginError = 'Erro interno. Tente novamente.'
      } finally {
        this.isLoggingIn = false
      }
    },

    handleLogout() {
      if (confirm('Tem certeza que deseja sair?')) {
        this.authService.logout()
        this.isAuthenticated = false
        this.session = null
        this.currentView = 'dashboard'
        this.currentModel = null
      }
    },

    selectModel(modelKey) {
      this.currentModel = modelKey
      this.currentView = 'model'
    }
  }
}

sections.style:
.admin-container {
  min-height: 100vh;
  background: #f8f9fa;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
}

/* Login Styles */
.admin-login {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.login-card {
  background: white;
  border-radius: 12px;
  padding: 40px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.2);
  width: 100%;
  max-width: 400px;
}

.login-header {
  text-align: center;
  margin-bottom: 30px;
}

.login-header h1 {
  margin: 0 0 10px 0;
  color: #333;
  font-size: 2em;
}

.login-header p {
  margin: 0;
  color: #666;
}

.login-form .form-group {
  margin-bottom: 20px;
}

.login-form label {
  display: block;
  margin-bottom: 5px;
  font-weight: 600;
  color: #333;
}

.form-control {
  width: 100%;
  padding: 12px;
  border: 2px solid #e1e5e9;
  border-radius: 8px;
  font-size: 16px;
  transition: border-color 0.3s;
  box-sizing: border-box;
}

.form-control:focus {
  outline: none;
  border-color: #007bff;
}

.btn {
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;
  text-decoration: none;
  display: inline-block;
  text-align: center;
}

.btn-primary {
  background: #007bff;
  color: white;
  width: 100%;
}

.btn-primary:hover {
  background: #0056b3;
}

.btn-primary:disabled {
  background: #6c757d;
  cursor: not-allowed;
}

.alert {
  padding: 12px;
  border-radius: 6px;
  margin-top: 15px;
}

.alert-danger {
  background: #f8d7da;
  color: #721c24;
  border: 1px solid #f5c6cb;
}

.login-help {
  text-align: center;
  margin-top: 20px;
  padding: 15px;
  background: #f8f9fa;
  border-radius: 8px;
  color: #666;
}

/* Dashboard Styles */
.admin-dashboard {
  display: grid;
  grid-template-areas: 
    "header header"
    "nav main";
  grid-template-columns: 250px 1fr;
  grid-template-rows: auto 1fr;
  min-height: 100vh;
}

.admin-header {
  grid-area: header;
  background: white;
  border-bottom: 1px solid #e1e5e9;
  padding: 0 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  height: 70px;
}

.header-left {
  display: flex;
  align-items: center;
  gap: 15px;
}

.header-left h1 {
  margin: 0;
  font-size: 1.5em;
  color: #333;
}

.version {
  background: #e9ecef;
  color: #6c757d;
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 0.8em;
}

.header-right {
  display: flex;
  align-items: center;
  gap: 15px;
}

.user-info {
  color: #666;
  font-weight: 500;
}

.btn-outline {
  background: transparent;
  color: #007bff;
  border: 2px solid #007bff;
}

.btn-outline:hover {
  background: #007bff;
  color: white;
}

.admin-nav {
  grid-area: nav;
  background: #343a40;
  color: white;
  padding: 20px 0;
  overflow-y: auto;
}

.nav-section {
  margin-bottom: 30px;
}

.nav-section h3 {
  padding: 0 20px;
  margin: 0 0 10px 0;
  font-size: 0.9em;
  color: #adb5bd;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.nav-item {
  display: flex;
  align-items: center;
  gap: 10px;
  width: 100%;
  padding: 12px 20px;
  background: none;
  border: none;
  color: #adb5bd;
  text-align: left;
  cursor: pointer;
  transition: all 0.3s;
  font-size: 14px;
}

.nav-item:hover {
  background: #495057;
  color: white;
}

.nav-item.active {
  background: #007bff;
  color: white;
}

.admin-main {
  grid-area: main;
  padding: 20px;
  overflow-y: auto;
}

/* Responsive */
@media (max-width: 768px) {
  .admin-dashboard {
    grid-template-areas: 
      "header"
      "nav"
      "main";
    grid-template-columns: 1fr;
    grid-template-rows: auto auto 1fr;
  }
  
  .admin-nav {
    max-height: 200px;
    overflow-y: auto;
  }
  
  .nav-section {
    margin-bottom: 15px;
  }
}

sections.script:
import IconComponent from '../../components/IconComponent.vue'

export default {
  name: 'AdminTools',
  components: {
    IconComponent
  },
  props: {
    models: {
      type: Object,
      required: true
    },
    dataService: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      exportFormat: 'json',
      selectedModels: [],
      cleanupOptions: {
        orphanedData: false,
        oldNotifications: false,
        deletedUsers: false,
        emptyPosts: false
      },
      generateOptions: {
        users: 10,
        posts: 50,
        comments: 100
      },
      showResultModal: false,
      resultModal: {
        title: '',
        type: '',
        data: null
      }
    }
  },
  computed: {
    storageInfo() {
      let totalUsed = 0
      for (const key in localStorage) {
        if (key.startsWith('ifwave_')) {
          totalUsed += localStorage[key].length
        }
      }
      
      // Estimativa baseada no limite t√≠pico de 5-10MB
      const estimatedLimit = 5 * 1024 * 1024 // 5MB
      const used = Math.round(totalUsed / 1024) // KB
      const available = Math.round((estimatedLimit - totalUsed) / 1024) // KB
      
      return { used, available }
    },
    
    totalRecords() {
      let total = 0
      for (const modelKey in this.models) {
        total += this.dataService.getAll(modelKey).length
      }
      return total
    },
    
    largestTable() {
      let largest = { name: 'Nenhuma', count: 0 }
      for (const [modelKey, model] of Object.entries(this.models)) {
        const count = this.dataService.getAll(modelKey).length
        if (count > largest.count) {
          largest = { name: model.name, count }
        }
      }
      return largest
    }
  },
  mounted() {
    this.selectedModels = Object.keys(this.models)
  },
  methods: {
    createBackup() {
      try {
        const backup = {
          version: '1.0.0',
          timestamp: new Date().toISOString(),
          data: {}
        }
        
        for (const modelKey in this.models) {
          backup.data[modelKey] = this.dataService.getAll(modelKey)
        }
        
        this.downloadFile(
          JSON.stringify(backup, null, 2),
          `ifwave-backup-${new Date().toISOString().split('T')[0]}.json`,
          'application/json'
        )
        
        this.showResult('Backup Criado', 'success', 'Backup realizado com sucesso!')
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro ao criar backup: ' + error.message)
      }
    },
    
    handleRestoreFile(event) {
      const file = event.target.files[0]
      if (!file) return
      
      const reader = new FileReader()
      reader.onload = (e) => {
        try {
          const backup = JSON.parse(e.target.result)
          this.restoreFromBackup(backup)
        } catch (error) {
          this.showResult('Erro', 'error', 'Arquivo de backup inv√°lido: ' + error.message)
        }
      }
      reader.readAsText(file)
    },
    
    restoreFromBackup(backup) {
      if (!backup.data) {
        this.showResult('Erro', 'error', 'Formato de backup inv√°lido')
        return
      }
      
      if (!confirm('Tem certeza que deseja restaurar o backup? Todos os dados atuais ser√£o substitu√≠dos.')) {
        return
      }
      
      try {
        for (const [modelKey, data] of Object.entries(backup.data)) {
          if (this.models[modelKey]) {
            localStorage.setItem(`ifwave_${modelKey}`, JSON.stringify(data))
          }
        }
        
        this.showResult('Restaura√ß√£o Conclu√≠da', 'success', 'Backup restaurado com sucesso!')
        this.refreshStats()
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro ao restaurar backup: ' + error.message)
      }
    },
    
    exportData() {
      try {
        const exportData = {}
        
        for (const modelKey of this.selectedModels) {
          exportData[modelKey] = this.dataService.getAll(modelKey)
        }
        
        let filename, content, mimeType
        
        switch (this.exportFormat) {
          case 'json':
            content = JSON.stringify(exportData, null, 2)
            filename = `ifwave-export-${new Date().toISOString().split('T')[0]}.json`
            mimeType = 'application/json'
            break
            
          case 'csv':
            content = this.convertToCSV(exportData)
            filename = `ifwave-export-${new Date().toISOString().split('T')[0]}.csv`
            mimeType = 'text/csv'
            break
            
          default:
            throw new Error('Formato de exporta√ß√£o n√£o suportado')
        }
        
        this.downloadFile(content, filename, mimeType)
        this.showResult('Exporta√ß√£o Conclu√≠da', 'success', 'Dados exportados com sucesso!')
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro ao exportar dados: ' + error.message)
      }
    },
    
    convertToCSV(data) {
      let csv = ''
      
      for (const [modelName, records] of Object.entries(data)) {
        if (records.length === 0) continue
        
        csv += `\n\n=== ${modelName.toUpperCase()} ===\n`
        
        const headers = Object.keys(records[0])
        csv += headers.join(',') + '\n'
        
        for (const record of records) {
          const values = headers.map(header => {
            const value = record[header]
            if (value === null || value === undefined) return ''
            if (typeof value === 'string') {
              return `"${value.replace(/"/g, '""')}"`
            }
            return value
          })
          csv += values.join(',') + '\n'
        }
      }
      
      return csv
    },
    
    handleImportFile(event) {
      const file = event.target.files[0]
      if (!file) return
      
      const reader = new FileReader()
      reader.onload = (e) => {
        try {
          let importData
          
          if (file.name.endsWith('.json')) {
            importData = JSON.parse(e.target.result)
          } else if (file.name.endsWith('.csv')) {
            // Implementa√ß√£o b√°sica de CSV - pode ser expandida
            this.showResult('Erro', 'error', 'Importa√ß√£o de CSV n√£o implementada ainda')
            return
          }
          
          this.importData(importData)
        } catch (error) {
          this.showResult('Erro', 'error', 'Erro ao importar arquivo: ' + error.message)
        }
      }
      reader.readAsText(file)
    },
    
    importData(data) {
      if (!confirm('Tem certeza que deseja importar estes dados? Registros existentes podem ser substitu√≠dos.')) {
        return
      }
      
      try {
        let imported = 0
        
        for (const [modelKey, records] of Object.entries(data)) {
          if (this.models[modelKey] && Array.isArray(records)) {
            for (const record of records) {
              this.dataService.create(modelKey, record)
              imported++
            }
          }
        }
        
        this.showResult('Importa√ß√£o Conclu√≠da', 'success', `${imported} registros importados com sucesso!`)
        this.refreshStats()
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro ao importar dados: ' + error.message)
      }
    },
    
    previewCleanup() {
      const preview = {}
      
      if (this.cleanupOptions.orphanedData) {
        preview['Dados √ìrf√£os'] = this.findOrphanedData()
      }
      
      if (this.cleanupOptions.oldNotifications) {
        preview['Notifica√ß√µes Antigas'] = this.findOldNotifications()
      }
      
      if (this.cleanupOptions.deletedUsers) {
        preview['Usu√°rios Inativos'] = this.findInactiveUsers()
      }
      
      if (this.cleanupOptions.emptyPosts) {
        preview['Posts Vazios'] = this.findEmptyPosts()
      }
      
      this.showResult('Pr√©via da Limpeza', 'cleanup-preview', preview)
    },
    
    performCleanup() {
      if (!confirm('Tem certeza que deseja executar a limpeza? Esta a√ß√£o n√£o pode ser desfeita.')) {
        return
      }
      
      try {
        let removed = 0
        
        if (this.cleanupOptions.orphanedData) {
          removed += this.removeOrphanedData()
        }
        
        if (this.cleanupOptions.oldNotifications) {
          removed += this.removeOldNotifications()
        }
        
        if (this.cleanupOptions.deletedUsers) {
          removed += this.removeInactiveUsers()
        }
        
        if (this.cleanupOptions.emptyPosts) {
          removed += this.removeEmptyPosts()
        }
        
        this.showResult('Limpeza Conclu√≠da', 'success', `${removed} itens removidos com sucesso!`)
        this.refreshStats()
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro durante a limpeza: ' + error.message)
      }
    },
    
    findOrphanedData() {
      // Encontrar dados sem refer√™ncias v√°lidas
      const orphaned = []
      // Implementa√ß√£o simplificada
      return orphaned
    },
    
    findOldNotifications() {
      const notifications = this.dataService.getAll('notifications')
      const thirtyDaysAgo = new Date()
      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30)
      
      return notifications.filter(notification => {
        const createdAt = new Date(notification.createdAt)
        return createdAt < thirtyDaysAgo
      })
    },
    
    findInactiveUsers() {
      const users = this.dataService.getAll('users')
      return users.filter(user => !user.isActive)
    },
    
    findEmptyPosts() {
      const posts = this.dataService.getAll('posts')
      return posts.filter(post => !post.content || post.content.trim() === '')
    },
    
    removeOrphanedData() {
      // Implementar remo√ß√£o de dados √≥rf√£os
      return 0
    },
    
    removeOldNotifications() {
      const oldNotifications = this.findOldNotifications()
      oldNotifications.forEach(notification => {
        this.dataService.delete('notifications', notification.id)
      })
      return oldNotifications.length
    },
    
    removeInactiveUsers() {
      const inactiveUsers = this.findInactiveUsers()
      inactiveUsers.forEach(user => {
        this.dataService.delete('users', user.id)
      })
      return inactiveUsers.length
    },
    
    removeEmptyPosts() {
      const emptyPosts = this.findEmptyPosts()
      emptyPosts.forEach(post => {
        this.dataService.delete('posts', post.id)
      })
      return emptyPosts.length
    },
    
    generateTestData() {
      if (!confirm('Tem certeza que deseja gerar dados de teste? Isso pode criar muitos registros.')) {
        return
      }
      
      try {
        let generated = 0
        
        // Gerar usu√°rios
        for (let i = 0; i < this.generateOptions.users; i++) {
          const user = this.generateRandomUser(i)
          this.dataService.create('users', user)
          generated++
        }
        
        // Gerar posts
        const users = this.dataService.getAll('users')
        for (let i = 0; i < this.generateOptions.posts; i++) {
          const post = this.generateRandomPost(users)
          this.dataService.create('posts', post)
          generated++
        }
        
        // Gerar coment√°rios
        const posts = this.dataService.getAll('posts')
        for (let i = 0; i < this.generateOptions.comments; i++) {
          const comment = this.generateRandomComment(users, posts)
          this.dataService.create('comments', comment)
          generated++
        }
        
        this.showResult('Dados Gerados', 'success', `${generated} registros de teste criados com sucesso!`)
        this.refreshStats()
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro ao gerar dados de teste: ' + error.message)
      }
    },
    
    generateRandomUser(index) {
      const names = ['Jo√£o', 'Maria', 'Pedro', 'Ana', 'Carlos', 'Luiza', 'Roberto', 'Fernanda', 'Ricardo', 'Camila']
      const surnames = ['Silva', 'Santos', 'Oliveira', 'Souza', 'Rodrigues', 'Ferreira', 'Alves', 'Pereira', 'Lima', 'Gomes']
      const courses = ['An√°lise e Desenvolvimento de Sistemas', 'Redes de Computadores', 'Inform√°tica', 'Administra√ß√£o', 'Contabilidade']
      const campuses = ['IFMT Campus Cuiab√°', 'IFMT Campus V√°rzea Grande', 'IFMT Campus Rondon√≥polis']
      
      const firstName = names[Math.floor(Math.random() * names.length)]
      const lastName = surnames[Math.floor(Math.random() * surnames.length)]
      const name = `${firstName} ${lastName}`
      
      return {
        name,
        email: `${firstName.toLowerCase()}.${lastName.toLowerCase()}${index}@estudante.ifmt.edu.br`,
        course: courses[Math.floor(Math.random() * courses.length)],
        campus: campuses[Math.floor(Math.random() * campuses.length)],
        passwordHash: this.hashPassword('123456'),
        isActive: Math.random() > 0.1, // 90% ativos
        isVerified: Math.random() > 0.3, // 70% verificados
        role: Math.random() > 0.9 ? 'teacher' : 'student', // 10% professores
        bio: `Estudante de ${courses[Math.floor(Math.random() * courses.length)]} no IFMT.`,
        createdAt: new Date(Date.now() - Math.random() * 90 * 24 * 60 * 60 * 1000).toISOString(), // √öltimos 90 dias
        lastLogin: new Date(Date.now() - Math.random() * 7 * 24 * 60 * 60 * 1000).toISOString() // √öltima semana
      }
    },
    
    generateRandomPost(users) {
      const contents = [
        'Acabei de terminar meu projeto de programa√ß√£o! üéâ',
        'Algu√©m pode me ajudar com esta quest√£o de matem√°tica?',
        'Que evento incr√≠vel tivemos hoje no campus!',
        'Dicas para a prova de algoritmos?',
        'Novo laborat√≥rio de inform√°tica ficou show! üíª',
        'Quem vai no evento de tecnologia na pr√≥xima semana?',
        'Compartilhando um artigo interessante sobre IA',
        'Grupo de estudos de banco de dados, quem tem interesse?',
        'Feedback sobre o projeto integrador',
        'D√∫vidas sobre est√°gio obrigat√≥rio'
      ]
      
      const user = users[Math.floor(Math.random() * users.length)]
      
      return {
        userId: user.id,
        content: contents[Math.floor(Math.random() * contents.length)],
        type: Math.random() > 0.8 ? 'image' : 'text',
        isPublic: Math.random() > 0.05, // 95% p√∫blicos
        likesCount: Math.floor(Math.random() * 50),
        commentsCount: Math.floor(Math.random() * 20),
        sharesCount: Math.floor(Math.random() * 10),
        createdAt: new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000).toISOString(), // √öltimos 30 dias
        updatedAt: new Date().toISOString()
      }
    },
    
    generateRandomComment(users, posts) {
      const comments = [
        'Muito bom! üëè',
        'Concordo totalmente!',
        'Tenho a mesma d√∫vida...',
        'Obrigado por compartilhar!',
        'Interessante ponto de vista',
        'Pode me explicar melhor?',
        'Excelente trabalho!',
        'Vou participar sim!',
        'Achei muito √∫til esta informa√ß√£o',
        'Parab√©ns pelo projeto!'
      ]
      
      const user = users[Math.floor(Math.random() * users.length)]
      const post = posts[Math.floor(Math.random() * posts.length)]
      
      return {
        postId: post.id,
        userId: user.id,
        content: comments[Math.floor(Math.random() * comments.length)],
        likesCount: Math.floor(Math.random() * 10),
        isHidden: Math.random() > 0.95, // 5% ocultos
        createdAt: new Date(Date.now() - Math.random() * 15 * 24 * 60 * 60 * 1000).toISOString(), // √öltimos 15 dias
        updatedAt: new Date().toISOString()
      }
    },
    
    clearAllData() {
      if (!confirm('ATEN√á√ÉO: Tem certeza que deseja limpar TODOS os dados? Esta a√ß√£o √© irrevers√≠vel!')) {
        return
      }
      
      if (!confirm('Esta √© sua √∫ltima chance. Todos os dados ser√£o perdidos permanentemente. Continuar?')) {
        return
      }
      
      try {
        for (const modelKey in this.models) {
          localStorage.removeItem(`ifwave_${modelKey}`)
        }
        
        this.showResult('Dados Limpos', 'success', 'Todos os dados foram removidos com sucesso!')
        this.refreshStats()
      } catch (error) {
        this.showResult('Erro', 'error', 'Erro ao limpar dados: ' + error.message)
      }
    },
    
    validateData() {
      const results = {}
      
      for (const [modelKey, model] of Object.entries(this.models)) {
        const records = this.dataService.getAll(modelKey)
        const errors = []
        
        records.forEach(record => {
          // Validar campos obrigat√≥rios
          for (const [fieldName, fieldConfig] of Object.entries(model.fields)) {
            if (fieldConfig.required && !record[fieldName]) {
              errors.push({
                id: record.id,
                message: `Campo obrigat√≥rio '${fieldConfig.label}' est√° vazio`
              })
            }
          }
          
          // Validar tipos de dados
          // Validar refer√™ncias estrangeiras
          // etc.
        })
        
        results[modelKey] = {
          total: records.length,
          errors
        }
      }
      
      this.showResult('Valida√ß√£o de Dados', 'validation', results)
    },
    
    showLocalStorageData() {
      const data = {}
      for (const key in localStorage) {
        if (key.startsWith('ifwave_')) {
          try {
            data[key] = JSON.parse(localStorage[key])
          } catch {
            data[key] = localStorage[key]
          }
        }
      }
      
      this.showResult('Dados do localStorage', 'storage', JSON.stringify(data, null, 2))
    },
    
    downloadLogs() {
      // Gerar logs do sistema
      const logs = {
        timestamp: new Date().toISOString(),
        system: 'IF Wave Admin',
        storage: this.storageInfo,
        records: this.totalRecords,
        models: Object.keys(this.models),
        browser: navigator.userAgent
      }
      
      this.downloadFile(
        JSON.stringify(logs, null, 2),
        `ifwave-logs-${new Date().toISOString().split('T')[0]}.json`,
        'application/json'
      )
      
      this.showResult('Logs Baixados', 'success', 'Logs do sistema baixados com sucesso!')
    },
    
    refreshStats() {
      this.$forceUpdate()
    },
    
    downloadFile(content, filename, mimeType) {
      const blob = new Blob([content], { type: mimeType })
      const url = URL.createObjectURL(blob)
      
      const link = document.createElement('a')
      link.href = url
      link.download = filename
      link.click()
      
      URL.revokeObjectURL(url)
    },
    
    hashPassword(password) {
      let hash = 0
      for (let i = 0; i < password.length; i++) {
        const char = password.charCodeAt(i)
        hash = ((hash << 5) - hash) + char
        hash = hash & hash
      }
      return Math.abs(hash).toString(16)
    },
    
    showResult(title, type, data) {
      this.resultModal = { title, type, data }
      this.showResultModal = true
    },
    
    closeResultModal() {
      this.showResultModal = false
      this.resultModal = { title: '', type: '', data: null }
    }
  }
}

