sections.script:
import RegisterForm from './components/RegisterForm.vue'
import LoginForm from './components/LoginForm.vue'
import FeedPosts from './components/Feed.vue'
import AdminPanel from './admin/components/AdminPanel.vue'

export default {
  name: 'App',
  components: {
    RegisterForm,
    LoginForm,
    FeedPosts,
    AdminPanel
  },
  data() {
    return {
      currentScreen: 'login',
      currentUser: null
    }
  },
  mounted() {
    // Verifica se h√° usu√°rio logado ao carregar a aplica√ß√£o
    this.checkSession()

    // Criar usu√°rio admin de exemplo se n√£o existir
    this.createAdminUser()
  },
  methods: {
    checkSession() {
      const userData = localStorage.getItem('ifwave_current_user')
      if (userData) {
        try {
          this.currentUser = JSON.parse(userData)
          this.currentScreen = 'feed'
        } catch (error) {
          // Se h√° erro no JSON, limpa os dados corrompidos
          localStorage.removeItem('ifwave_current_user')
        }
      }
    },

    handleLogin(userData) {
      this.currentUser = userData
      this.goToFeed()
    },

    logout() {
      this.currentUser = null
      localStorage.removeItem('ifwave_current_user')
      this.currentScreen = 'login'

      // Feedback visual do logout
      setTimeout(() => {
        alert('Logout realizado com sucesso!')
      }, 100)
    },

    goToLogin() {
      this.currentScreen = 'login'
    },

    goToFeed() {
      this.currentScreen = 'feed'
    },

    toggleDarkMode() {
      document.body.classList.toggle('dark-mode')
    },

    openAdmin() {
      // Alternar para tela do painel admin
      this.currentScreen = 'admin'
    },

    goBackFromAdmin() {
      this.currentScreen = 'feed'
    },

    handleRegistration(userData) {
      // Usu√°rio foi cadastrado e logado automaticamente
      this.currentUser = userData
      this.goToFeed()

      // Verificar se os dados est√£o realmente salvos
      const savedUser = JSON.parse(localStorage.getItem('ifwave_current_user') || 'null')
      if (!savedUser) {
        console.warn('Usu√°rio n√£o encontrado no localStorage ap√≥s registro')
        // Recriar dados se necess√°rio
        localStorage.setItem('ifwave_current_user', JSON.stringify(userData))
      }
    },

    createAdminUser() {
      // Criar usu√°rio admin se n√£o existir
      const users = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
      const adminExists = users.find(user => user.role === 'admin')

      if (!adminExists) {
        const adminUser = {
          id: 'admin-' + Date.now(),
          name: 'Administrador',
          email: 'admin@ifmt.edu.br',
          username: 'admin',
          password: 'admin123',
          role: 'admin',
          course: 'Administra√ß√£o',
          campus: 'Cuiab√°',
          bio: 'Administrador do sistema IF Wave',
          avatar: null,
          isActive: true,
          emailVerified: true,
          preferences: {
            theme: 'light',
            notifications: true,
            privacy: 'public'
          },
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString()
        }

        users.push(adminUser)
        localStorage.setItem('ifwave_users', JSON.stringify(users))
        console.log('Usu√°rio admin criado:', adminUser.email)

        // Criar dados de exemplo
        this.createSampleData()
      }
    },

    createSampleData() {
      // Criar usu√°rios de exemplo
      const users = JSON.parse(localStorage.getItem('ifwave_users') || '[]')
      const sampleUsers = [
        {
          id: 'user-1',
          name: 'Jo√£o Silva',
          email: 'joao@ifmt.edu.br',
          username: 'joao.silva',
          password: 'senha123',
          role: 'student',
          course: 'Inform√°tica',
          campus: 'Cuiab√°',
          bio: 'Estudante de inform√°tica',
          isActive: true,
          emailVerified: true,
          createdAt: new Date().toISOString()
        },
        {
          id: 'user-2', 
          name: 'Maria Santos',
          email: 'maria@ifmt.edu.br',
          username: 'maria.santos',
          password: 'senha123',
          role: 'teacher',
          course: 'Matem√°tica',
          campus: 'V√°rzea Grande',
          bio: 'Professora de matem√°tica',
          isActive: true,
          emailVerified: true,
          createdAt: new Date().toISOString()
        }
      ]

      sampleUsers.forEach(user => {
        if (!users.find(u => u.email === user.email)) {
          users.push(user)
        }
      })
      localStorage.setItem('ifwave_users', JSON.stringify(users))

      // Criar posts de exemplo
      const posts = JSON.parse(localStorage.getItem('ifwave_posts') || '[]')
      if (posts.length === 0) {
        const samplePosts = [
          {
            id: 'post-1',
            content: 'Bem-vindos ao IF Wave! üéâ',
            userId: 'user-1',
            type: 'text',
            likesCount: 15,
            commentsCount: 3,
            isPublic: true,
            createdAt: new Date().toISOString()
          },
          {
            id: 'post-2',
            content: 'Terminei meu TCC! üìö',
            userId: 'user-2',
            type: 'text',
            likesCount: 8,
            commentsCount: 2,
            isPublic: true,
            createdAt: new Date().toISOString()
          }
        ]
        localStorage.setItem('ifwave_posts', JSON.stringify(samplePosts))
      }

      console.log('Dados de exemplo criados!')
    },
  }
}

sections.style:
:root {
  --azul-ifmt: #003D7C;
  --verde-energia: #2ECC71;
  --amarelo-alegria: #F1C40F;
  --roxo-inovador: #8E44AD;
  --branco-puro: #FFFFFF;
  --preto-profundo: #000000;

  --background: var(--branco-puro);
  --foreground: var(--preto-profundo);
  --card: #fff;
  --border: #dbdbdb;
  --primary: var(--azul-ifmt);
  --secondary: var(--verde-energia);
  --accent: var(--amarelo-alegria);
  --special: var(--roxo-inovador);
}

body.dark-mode {
  --background: #18191a;
  --foreground: #f5f6fa;
  --card: #242526;
  --border: #333;
}

body {
  background: var(--background);
  color: var(--foreground);
  transition: background 0.3s, color 0.3s;
}

#app {
  text-align: center;
  margin-top: 40px;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
  margin-bottom: 20px;
}

.header h1 {
  margin: 0;
  flex-grow: 1;
}

.header-controls {
  display: flex;
  gap: 10px;
  align-items: center;
}

.dark-btn, .logout-btn, .admin-btn {
  background: var(--primary);
  color: var(--branco-puro);
  border: 1px solid var(--border);
  border-radius: 6px;
  padding: 8px 16px;
  cursor: pointer;
  font-weight: 600;
  font-size: 13px;
  transition: all 0.2s ease;
  white-space: nowrap;
}

.admin-btn {
  background: var(--special);
}

.admin-btn:hover {
  background: #7239ca;
}

.dark-btn:hover {
  background: var(--roxo-inovador);
}

.logout-btn {
  background: #ed4956;
}

.logout-btn:hover {
  background: #c73e47;
}

@media (max-width: 768px) {
  .header {
    flex-direction: column;
    gap: 10px;
    text-align: center;
  }

  .header-controls {
    justify-content: center;
  }

  .dark-btn, .logout-btn {
    padding: 6px 12px;
    font-size: 12px;
  }
}
